import React, { useState, useEffect } from "react";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";
import { useNavigate } from "react-router-dom";
import img from '../Components/img3.JPG';
import { useLanguage } from '../Store/LanguageContext';
import logo from '../Components/lo.png';

// استيراد من مكتبات التحقق
import { useForm } from "react-hook-form";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from "yup";

// استدعاء الـ schema الخاص بالتحقق (تقدر تستبدل هذا بملف خارجي عندك)
const getFeedbackSchemaValidation = (lang = "en") => {
  const isArabic = lang === "ar";
  return yup.object().shape({
    name: yup
      .string()
      .required(isArabic ? "الاسم مطلوب" : "Name is required")
      .min(2, isArabic ? "الاسم يجب أن يكون على الأقل حرفين" : "Name must be at least 2 characters long"),
    number: yup
      .string()
      .required(isArabic ? "رقم الهاتف مطلوب" : "Phone number is required")
      .matches(/^\d{8}$/, isArabic ? "رقم الهاتف غير صحيح (يجب أن يكون من 8 أرقام)" : "Phone number must be exactly 8 digits"),
    event: yup
      .string()
      .required(isArabic ? "اسم الفعالية مطلوب" : "Event name is required")
      .min(3, isArabic ? "اسم الفعالية يجب أن يكون 3 أحرف على الأقل" : "Event name must be at least 3 characters long"),
    rating: yup
      .number()
      .required(isArabic ? "التقييم مطلوب" : "Rating is required")
      .min(1, isArabic ? "التقييم يجب أن يكون بين 1 و 5" : "Rating must be between 1 and 5")
      .max(5, isArabic ? "التقييم يجب أن يكون بين 1 و 5" : "Rating must be between 1 and 5"),
    comments: yup
      .string()
      .required(isArabic ? "التعليق مطلوب" : "Comments are required")
      .min(5, isArabic ? "يرجى كتابة تعليق مفيد على الأقل 5 أحرف" : "Please enter a comment of at least 5 characters"),
  });
};

const Feed = () => {
  const navigate = useNavigate();
  const { lang, setLang } = useLanguage();

  const [events, setEvents] = useState([]);
  const [submitted, setSubmitted] = useState(false);
  const [submitError, setSubmitError] = useState("");

  // جلب الأحداث
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const res = await fetch('http://localhost:3001/events');
        if (!res.ok) throw new Error('Failed to fetch events');
        const data = await res.json();
        setEvents(data);
      } catch (err) {
        setEvents([
          { en: "Share your opinion, because your opinion matters! ⭐", ar: "⭐ شاركنا رأيك، لأن رأيك يفرق " },
        ]);
      }
    };
    fetchEvents();
  }, []);

  // schema التحقق حسب اللغة
  const schema = getFeedbackSchemaValidation(lang);

  // useForm مع yup
  const {
    register,
    handleSubmit,
    setValue,
    watch,
    formState: { errors, isSubmitting }
  } = useForm({
    resolver: yupResolver(schema),
    mode: "onBlur",
    defaultValues: {
      name: "",
      number: "",
      event: "",
      rating: 0,
      comments: "",
    },
  });

  // مراقبة تقييم النجوم للتحكم اليدوي في الواجهة
  const rating = watch("rating");

  // التبديل بين اللغات
  const toggleLang = () => setLang(lang === "en" ? "ar" : "en");

  // إرسال النموذج
  const onSubmit = async (data) => {
    setSubmitError("");
    try {
      const res = await fetch('http://localhost:3001/feedback', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });
      if (!res.ok) {
        const result = await res.json();
        setSubmitError(result.msg || (lang === 'ar' ? 'حدث خطأ أثناء الإرسال' : 'Submission failed'));
        return;
      }
      setSubmitted(true);
    } catch (err) {
      setSubmitError(lang === 'ar' ? 'تعذر الاتصال بالخادم' : 'Could not connect to server');
    }
  };

  // إعادة التوجيه بعد الإرسال بنجاح
  useEffect(() => {
    if (submitted) {
      const eventParam = encodeURIComponent(watch("event"));
      navigate(`/admin/feedback?event=${eventParam}`);
    }
  }, [submitted, navigate, watch]);

  const t = {
    en: {
      formTitle: 'Share your opinion, because your opinion matters! ⭐',
      yourName: 'Your Name',
      namePlaceholder: 'Enter your full name',
      eventName: 'Event Name',
      selectEvent: '-- Select an Event --',
      rating: 'Rating (1 to 5)',
      comments: 'Additional Comments',
      commentsPlaceholder: 'Share your thoughts...',
      submit: 'Submit',
      thankYou: 'Thank you for your feedback!',
      help: 'Your opinion helps us improve our events.',
      yourPhone: "Your Phone Number",
      phonePlaceholder: "Enter your phone number",
      chooseLang: 'عربي',
    },
    ar: {
      formTitle: '⭐شاركنا رأيك، لأن رأيك يفرق ',
      yourName: 'اسمك',
      namePlaceholder: 'أدخل اسمك الكامل',
      eventName: 'اسم الفعالية',
      selectEvent: '-- اختر فعالية --',
      rating: 'التقييم (1 إلى 5)',
      comments: 'تعليقات إضافية',
      commentsPlaceholder: 'شاركنا رأيك...',
      submit: 'تسليم ',
      thankYou: 'شكراً لملاحظاتك!',
      help: 'رأيك يساعدنا في تحسين فعالياتنا.',
      yourPhone: "رقم الهاتف",
      phonePlaceholder: "أدخل رقم هاتفك",
      chooseLang: 'English',
    }
  };

  // دالة لتحديث تقييم النجوم عند النقر
  const handleStarClick = (star) => {
    setValue("rating", star, { shouldValidate: true });
  };

  return (
    <Container
      className="justify-content-center"
      style={{
        backgroundImage: `url(${img})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
        minHeight: '90vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontFamily: 'Tajawal, Cairo, Roboto, Arial, sans-serif',
        color: '#232336',
      }}
    >
      <img src={logo} alt="Engineering Village Logo" style={{ position: 'absolute', top: 18, left: 18, width: 150, height: 'auto', zIndex: 100 }} />

      <Row className="justify-content-center" style={{ position: 'relative', zIndex: 1 }}>
        <Col md={8} lg={6}>
          {/* عنوان ونص */}
          <div
            style={{
              background: 'linear-gradient(120deg, #f7f3ff 0%, #e3f0ff 100%)',
              borderRadius: '18px',
              boxShadow: '0 6px 32px rgba(45,25,118,0.13)',
              padding: '1.7rem 1.7rem 1.2rem 1.7rem',
              marginBottom: '0.7rem',
              textAlign: 'center',
              maxWidth: '1200px',
              minWidth: '500px',
              border: '1.5px solid #d1c4e9',
              marginTop: '2.5rem',
              fontFamily: 'inherit',
            }}
          >
            <div style={{
              fontSize:'2.3rem',
              fontWeight:900,
              color:'#4b2996',
              letterSpacing:'1.2px',
              textShadow:'0 2px 8px #b39ddb44',
              marginBottom:'0.2rem',
              display:'inline-block',
            }}>
              <span role="img" aria-label="party"></span> {t[lang].formTitle} <span role="img" aria-label="sparkle"></span>
              <div style={{
                height:'3px',
                width:'80%',
                background:'linear-gradient(90deg, #4b2996 0%, #1a4d8f 100%)',
                borderRadius:'2px',
                margin:'0.5rem auto 0 auto',
                opacity:0.7,
              }}></div>
            </div>
            <div style={{
              color:'#1a4d8f',
              fontWeight:700,
              fontSize:'1.18rem',
              marginTop:'0.7rem',
              letterSpacing:'0.3px',
              textShadow:'0 1px 4px #b3c6e744',
            }}>
              {lang === 'ar'
                ? 'كل كلمة منك تصنع فرق كبير في فعالياتنا القادمة 🎯'
                : 'Every word from you makes a big difference in our upcoming events 🎯'}
            </div>
          </div>

          {/* الزر لتبديل اللغة */}
          <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
            <Button
              size="sm"
              color="secondary"
              onClick={toggleLang}
              style={{
                borderRadius: '20px',
                background: 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)',
                color: '#fff',
                fontWeight: 600,
                boxShadow: '0 2px 8px rgba(79, 140, 255, 0.12)',
                border: 'none',
                padding: '0.5rem 1.5rem',
                transition: 'background 0.2s, box-shadow 0.2s',
                letterSpacing: '0.5px',
                outline: 'none',
                cursor: 'pointer',
              }}
              onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae8 0%, #7bb6fa 100%)'}
              onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)'}
            >
              {t[lang].chooseLang}
            </Button>
          </div>

          {/* نموذج التقييم */}
          <Form onSubmit={handleSubmit(onSubmit)} noValidate>
            {submitError && (
              <div style={{ color: '#c00', fontWeight: 700, textAlign: 'center', marginTop: 12 }}>{submitError}</div>
            )}

            {/* الاسم */}
            <FormGroup>
              <Label for="name" style={{
                fontWeight:700,
                color:'#2d1976',
                marginBottom:'0.2rem',
                fontSize:'1.15rem',
                letterSpacing:'0.2px',
                display:'flex',
                alignItems:'center',
                gap:'0.3rem',
              }}>
                <span role="img" aria-label="user">🧑</span> {t[lang].yourName}
              </Label>
              <Input
                id="name"
                {...register("name")}
                placeholder={t[lang].namePlaceholder}
                invalid={!!errors.name}
                style={{
                  background: 'rgba(255,255,255,0.99)',
                  borderRadius: '18px',
                  padding: '1.15rem 1.2rem',
                  fontSize: '1.13rem',
                  marginBottom: '1.2rem',
                  boxShadow: '0 2px 12px rgba(255, 107, 203, 0.10)',
                  outline: 'none',
                  border: errors.name ? '2px solid #c00' : '2px solid #ffb86c',
                  fontFamily: 'inherit',
                  width: '85%',
                  minWidth: '220px',
                }}
              />
              {errors.name && (
                <div style={{ color: '#c00', fontWeight: 600, fontSize: '1rem', marginTop: '-0.8rem', marginBottom: '0.7rem', textAlign: 'left' }}>
                  {errors.name.message}
                </div>
              )}
            </FormGroup>

            {/* رقم الهاتف */}
            <FormGroup>
              <Label for="number" style={{
                fontWeight: 700,
                color: '#2d1976',
                marginBottom: '0.2rem',
                fontSize: '1.15rem',
                letterSpacing: '0.2px',
                display: 'flex',
                alignItems: 'center',
                gap: '0.3rem',
              }}>
                <span role="img" aria-label="phone">📱</span> {t[lang].yourPhone}
              </Label>
              <Input
                id="number"
                {...register("number")}
                placeholder={t[lang].phonePlaceholder}
                invalid={!!errors.number}
                style={{
                  background: 'rgba(255,255,255,0.99)',
                  borderRadius: '18px',
                  padding: '1.15rem 1.2rem',
                  fontSize: '1.13rem',
                  marginBottom: '1.2rem',
                  boxShadow: '0 2px 12px rgba(255, 107, 203, 0.10)',
                  outline: 'none',
                  border: errors.number ? '2px solid #c00' : '2px solid #ffb86c',
                  fontFamily: 'inherit',
                  width: '85%',
                  minWidth: '220px',
                }}
              />
              {errors.number && (
                <div style={{
                  color: '#c00',
                  fontWeight: 600,
                  fontSize: '1rem',
                  marginTop: '-0.8rem',
                  marginBottom: '0.7rem',
                  textAlign: 'left'
                }}>
                  {errors.number.message}
                </div>
              )}
            </FormGroup>

            {/* اسم الفعالية */}
            <FormGroup>
              <Label for="event" style={{
                fontWeight:700,
                color:'#1a4d8f',
                marginBottom:'0.3rem',
                fontSize:'1.15rem',
                letterSpacing:'0.2px',
                display:'flex',
                alignItems:'center',
                gap:'0.3rem',
              }}>
                <span role="img" aria-label="event">📅</span> {t[lang].eventName}
              </Label>
              <Input
                type="select"
                id="event"
                {...register("event")}
                invalid={!!errors.event}
                style={{
                  background: 'rgba(255,255,255,0.99)',
                  borderRadius: '18px',
                  padding: '1.15rem 1.2rem',
                  fontSize: '1.13rem',
                  marginBottom: '1.2rem',
                  boxShadow: '0 2px 12px rgba(255, 107, 203, 0.10)',
                  outline: 'none',
                  border: errors.event ? '2px solid #c00' : '2px solid #ffb86c',
                  fontFamily: 'inherit',
                  width: '85%',
                  minWidth: '220px',
                }}
              >
                <option value="">{t[lang].selectEvent}</option>
                {events.map((ev, idx) => (
                  <option key={ev.en + ev.ar + idx} value={lang === 'ar' ? ev.ar : ev.en}>
                    {lang === 'ar' ? ev.ar : ev.en}
                  </option>
                ))}
              </Input>
              {errors.event && (
                <div style={{ color: '#c00', fontWeight: 600, fontSize: '1rem', marginTop: '-0.8rem', marginBottom: '0.7rem', textAlign: 'left' }}>
                  {errors.event.message}
                </div>
              )}
            </FormGroup>

            {/* التقييم بالنجوم */}
            <FormGroup>
              <Label style={{
                fontWeight:700,
                color:'#e67e22',
                marginBottom:'0.3rem',
                fontSize:'1.15rem',
                letterSpacing:'0.2px',
                display:'flex',
                alignItems:'center',
                gap:'0.3rem',
              }}>
                <span role="img" aria-label="star">⭐</span> {t[lang].rating}
              </Label>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '1.2rem', userSelect: 'none' }}>
                {[1,2,3,4,5].map(star => (
                  <span
                    key={star}
                    onClick={() => handleStarClick(star)}
                    style={{
                      fontSize: '2.1rem',
                      cursor: 'pointer',
                      color: star <= rating ? '#ffb400' : '#e0e0e0',
                      transition: 'color 0.2s',
                      filter: star <= rating ? 'drop-shadow(0 2px 6px #ffb40044)' : 'none',
                    }}
                    role="img"
                    aria-label={star + ' star'}
                  >
                    ★
                  </span>
                ))}
              </div>
              {errors.rating && (
                <div style={{ color: '#c00', fontWeight: 600, fontSize: '1rem', marginTop: '-0.8rem', marginBottom: '0.7rem', textAlign: 'left' }}>
                  {errors.rating.message}
                </div>
              )}
            </FormGroup>

            {/* التعليقات */}
            <FormGroup>
              <Label for="comments" style={{
                fontWeight:700,
                color:'#2d1976',
                marginBottom:'0.2rem',
                fontSize:'1.15rem',
                letterSpacing:'0.2px',
                display:'flex',
                alignItems:'center',
                gap:'0.3rem',
              }}>
                <span role="img" aria-label="comments">💬</span> {t[lang].comments}
              </Label>
              <Input
                type="textarea"
                id="comments"
                rows={4}
                {...register("comments")}
                placeholder={t[lang].commentsPlaceholder}
                invalid={!!errors.comments}
                style={{
                  background: 'rgba(255,255,255,0.99)',
                  borderRadius: '18px',
                  padding: '1.15rem 1.2rem',
                  fontSize: '1.13rem',
                  marginBottom: '1.2rem',
                  boxShadow: '0 2px 12px rgba(255, 107, 203, 0.10)',
                  outline: 'none',
                  border: errors.comments ? '2px solid #c00' : '2px solid #ffb86c',
                  fontFamily: 'inherit',
                }}
              />
              {errors.comments && (
                <div style={{ color: '#c00', fontWeight: 600, fontSize: '1rem', marginTop: '-0.8rem', marginBottom: '0.7rem', textAlign: 'left' }}>
                  {errors.comments.message}
                </div>
              )}
            </FormGroup>

            <Button
              type="submit"
              color="primary"
              disabled={isSubmitting}
              style={{
                background: 'linear-gradient(90deg, #2b71e9 0%, #6ca0ff 100%)',
                borderRadius: '30px',
                fontWeight: 600,
                fontSize: '1.2rem',
                padding: '0.9rem 2rem',
                border: 'none',
                boxShadow: '0 8px 20px rgba(34,56,196,0.27)',
                cursor: isSubmitting ? 'not-allowed' : 'pointer',
                color: '#fff',
                width: '100%',
                marginTop: '1rem',
                transition: 'all 0.3s ease-in-out',
              }}
              onMouseOver={e => !isSubmitting && (e.currentTarget.style.background = 'linear-gradient(90deg, #1546a0 0%, #3d66d5 100%)')}
              onMouseOut={e => !isSubmitting && (e.currentTarget.style.background = 'linear-gradient(90deg, #2b71e9 0%, #6ca0ff 100%)')}
            >
              {t[lang].submit}
            </Button>
          </Form>

          {/* رسالة الشكر بعد الإرسال */}
          {submitted && (
            <div
              style={{
                marginTop: '2rem',
                textAlign: 'center',
                fontSize: '1.3rem',
                color: '#1a4d8f',
                fontWeight: '700',
              }}
            >
              {t[lang].thankYou}<br />
              <small style={{ fontWeight: '400', color: '#556b91' }}>{t[lang].help}</small>
            </div>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default Feed;
