import React, { useState } from "react";
import { Form, FormGroup, Label, Input, Button, Container, Alert } from "reactstrap";

const Feed = () => {
    const [formData, setFormData] = useState({
        name: "",
        event: "",
        rating: "",
        comments: "",
      });

      const [submitted, setSubmitted] = useState(false);

      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Submitted:", formData);
        setSubmitted(true);
      };
  if (submitted) {
    return (
      <Container className="py-5">
        <Alert color="success" className="text-center">
          <h2 className="mb-2">Thank you for your feedback!</h2>
          <p>We appreciate your input.</p>
        </Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-5 p-4 shadow rounded" style={{ maxWidth: 500, background: "#f8f9fa" }}>
      <h1 className="mb-4 text-center">Event Evaluation Form</h1>
      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <Label for="name">Your Name</Label>
          <Input type="text" name="name" id="name" value={formData.name} onChange={handleChange} required />
        </FormGroup>
        <FormGroup>
          <Label for="event">Event Name</Label>
          <Input type="select" name="event" id="event" value={formData.event} onChange={handleChange} required>
            <option value="">Select an event</option>
            <option value="Engineering Day">Engineering Day</option>
            <option value="Orientation Session">Orientation Session</option>
            <option value="Workshop">Workshop</option>
          </Input>
        </FormGroup>
        <FormGroup>
          <Label for="rating">Rating (1â€“5)</Label>
          <Input type="number" name="rating" id="rating" min="1" max="5" value={formData.rating} onChange={handleChange} required />
        </FormGroup>
        <FormGroup>
          <Label for="comments">Comments</Label>
          <Input type="textarea" name="comments" id="comments" rows="4" value={formData.comments} onChange={handleChange} />
        </FormGroup>
        <Button color="primary" block>Submit Evaluation</Button>
      </Form>
    </Container>
  );
};

export default Feed;
