import React from 'react';
import { useLanguage } from '../Store/LanguageContext';
import logo from '../Components/lo.png';
import { useNavigate } from 'react-router-dom';

const Header = ({ onAdminClick }) => {
  const { lang, setLang } = useLanguage();
  const navigate = useNavigate();
  const toggleLang = () => setLang(lang === 'en' ? 'ar' : 'en');

  return (
    <header
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        backdropFilter: 'blur(10px)',
        backgroundColor: 'rgba(255, 255, 255, 0.6)',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
        zIndex: 10000,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0.5rem 2rem',
        height: 70,
        borderBottom: '1px solid rgba(0,0,0,0.05)',
      }}
    >
      {/* Logo */}
      <img
        src={logo}
        alt="Engineering Village Logo"
        style={{ height: 45, cursor: 'pointer' }}
        onClick={() => navigate('/')}
      />

      {/* Buttons group */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
        {/* Admin button */}
        <button
          onClick={onAdminClick}
          title="Admin Login"
          aria-label="Admin Menu"
          style={{
            background: 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)',
            color: '#fff',
            border: 'none',
            borderRadius: '50%',
            width: 45,
            height: 45,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '1.5rem',
            boxShadow: '0 2px 6px rgba(166, 124, 82, 0.2)',
            cursor: 'pointer',
            transition: 'transform 0.2s',
          }}
          onMouseOver={(e) =>
            (e.currentTarget.style.background =
              'linear-gradient(90deg, #e9dbc7 0%, #a67c52 100%)')
          }
          onMouseOut={(e) =>
            (e.currentTarget.style.background =
              'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)')
          }
        >
          ðŸ”’
        </button>

        {/* Language toggle button */}
        <button
          onClick={toggleLang}
          style={{
            background: 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)',
            color: '#fff',
            border: 'none',
            borderRadius: 25,
            padding: '0.5rem 1.2rem',
            fontWeight: 600,
            fontSize: '1rem',
            boxShadow: '0 2px 6px rgba(79, 140, 255, 0.15)',
            cursor: 'pointer',
            transition: 'background 0.2s',
          }}
          onMouseOver={(e) =>
            (e.currentTarget.style.background =
              'linear-gradient(90deg, #357ae8 0%, #7bb6fa 100%)')
          }
          onMouseOut={(e) =>
            (e.currentTarget.style.background =
              'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)')
          }
        >
          {lang === 'ar' ? 'English' : 'Ø¹Ø±Ø¨ÙŠ'}
        </button>
      </div>
    </header>
  );
};

export default Header;
