import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useLanguage } from '../Store/LanguageContext';

const AdminAddEvent = () => {
  const [eventNameEn, setEventNameEn] = useState("");
  const [eventNameAr, setEventNameAr] = useState("");
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const { lang, setLang } = useLanguage();

  useEffect(() => {
    if (submitted) {
      const timer = setTimeout(() => {
        navigate("/");
      }, 1000);
      return () => clearTimeout(timer);
    }
  }, [submitted, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    if (eventNameEn.trim() && eventNameAr.trim()) {
      try {
        const res = await fetch("http://localhost:5000/events", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ en: eventNameEn, ar: eventNameAr })
        });
        if (!res.ok) {
          const data = await res.json();
          setError(data.msg || "Error adding event");
          return;
        }
        setSubmitted(true);
      } catch (err) {
        setError("Network error. Please try again.");
      }
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "linear-gradient(135deg, #f3e9dd 0%, #e9dbc7 35%, #f7f3e9 70%, #e2c9a7 100%)",
        fontFamily: "Tajawal, Cairo, Roboto, sans-serif",
      }}
    >
      <div
        style={{
          background: "rgba(255,255,255,0.92)",
          borderRadius: "32px",
          boxShadow: "0 8px 40px rgba(30, 64, 175, 0.10)",
          padding: "2.5rem 2rem",
          border: "2px solid #e9dbc7",
          minWidth: "350px",
          maxWidth: "420px",
          width: "100%",
        }}
      >
        <h2 style={{ textAlign: "center", color: "#a67c52", fontWeight: 800, marginBottom: "2rem" }}>
          Add New Event
        </h2>
        {submitted ? (
          <div style={{ textAlign: "center", color: "#43a047", fontWeight: 700, fontSize: "1.2rem" }}>
            Event "{eventNameEn}" added successfully!<br/>
            <span style={{fontSize:'0.95rem', color:'#888'}}>Redirecting...</span>
          </div>
        ) : (
          <form onSubmit={handleSubmit}>
            <label htmlFor="eventNameEn" style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.1rem" }}>
              Event Name (English)
            </label>
            <input
              id="eventNameEn"
              type="text"
              value={eventNameEn}
              onChange={(e) => setEventNameEn(e.target.value)}
              placeholder="Enter event name in English"
              style={{
                width: "100%",
                padding: "1rem",
                borderRadius: "14px",
                border: "1.5px solid #e9dbc7",
                margin: "1rem 0 1.2rem 0",
                fontSize: "1.1rem",
                outline: "none",
                background: "#f7f3e9",
                transition: "border-color 0.2s",
              }}
              onFocus={e => e.target.style.border = '1.5px solid #a67c52'}
              onBlur={e => e.target.style.border = '1.5px solid #e9dbc7'}
              required
            />
            <label htmlFor="eventNameAr" style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.1rem" }}>
              اسم الفعالية (بالعربي)
            </label>
            <input
              id="eventNameAr"
              type="text"
              value={eventNameAr}
              onChange={(e) => setEventNameAr(e.target.value)}
              placeholder="أدخل اسم الفعالية بالعربي"
              style={{
                width: "100%",
                padding: "1rem",
                borderRadius: "14px",
                border: "1.5px solid #e9dbc7",
                margin: "1rem 0 1.5rem 0",
                fontSize: "1.1rem",
                outline: "none",
                background: "#f7f3e9",
                transition: "border-color 0.2s",
              }}
              onFocus={e => e.target.style.border = '1.5px solid #a67c52'}
              onBlur={e => e.target.style.border = '1.5px solid #e9dbc7'}
              required
            />
            <button
              type="submit"
              style={{
                width: "100%",
                padding: "1rem 0",
                borderRadius: "999px",
                background: "linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)",
                color: "#fff",
                fontWeight: 800,
                fontSize: "1.15rem",
                border: "none",
                boxShadow: "0 2px 8px rgba(166, 124, 82, 0.10)",
                cursor: "pointer",
                transition: "background 0.2s, transform 0.13s",
              }}
              onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #e9dbc7 0%, #a67c52 100%)'}
              onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)'}
            >
              Add Event
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default AdminAddEvent;
