import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const AdminViewFeedback = () => {
  const [feedbacks, setFeedbacks] = useState([]);
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const eventFilter = params.get('event');
  const [deleteMsg, setDeleteMsg] = useState("");
  const [adminUnlocked, setAdminUnlocked] = useState(false);
  const [showAdminLogin, setShowAdminLogin] = useState(false);
  const [adminPassword, setAdminPassword] = useState("");
  const [adminError, setAdminError] = useState("");
  const ADMIN_PASSWORD = "admin123";

  useEffect(() => {
    const stored = localStorage.getItem("feedbacks");
    if (stored) {
      let all = JSON.parse(stored);
      if (eventFilter) {
        all = all.filter(fb => fb.event === eventFilter);
      }
      setFeedbacks(all);
    }
  }, [eventFilter]);

  // Delete feedback by index (in filtered list)
  const handleDelete = (idx) => {
    if (!window.confirm('Are you sure you want to delete this feedback?')) return;
    const all = JSON.parse(localStorage.getItem('feedbacks') || '[]');
    let filtered = all;
    if (eventFilter) {
      filtered = all.filter(fb => fb.event === eventFilter);
    }
    const fbToDelete = filtered[idx];
    const indexInAll = all.findIndex(fb =>
      fb.name === fbToDelete.name &&
      fb.event === fbToDelete.event &&
      fb.rating === fbToDelete.rating &&
      fb.comments === fbToDelete.comments
    );
    if (indexInAll !== -1) {
      all.splice(indexInAll, 1);
      localStorage.setItem('feedbacks', JSON.stringify(all));
      let newFiltered = all;
      if (eventFilter) {
        newFiltered = all.filter(fb => fb.event === eventFilter);
      }
      setFeedbacks(newFiltered);
      setDeleteMsg(document.documentElement.dir === 'rtl' || document.body.dir === 'rtl' ? 'تم الحذف بنجاح' : 'Deleted successfully');
      setTimeout(() => setDeleteMsg(""), 1500);
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(135deg, #f3e9dd 0%, #e9dbc7 35%, #f7f3e9 70%, #e2c9a7 100%)",
        fontFamily: "Tajawal, Cairo, Roboto, sans-serif",
        padding: "2rem 0",
      }}
    >
      <div style={{ maxWidth: 900, margin: "0 auto" }}>
        {deleteMsg && (
          <div style={{
            background: '#43a047',
            color: '#fff',
            borderRadius: 10,
            padding: '0.7rem 1.2rem',
            textAlign: 'center',
            fontWeight: 700,
            fontSize: '1.1rem',
            marginBottom: 18,
            boxShadow: '0 2px 8px rgba(67,160,71,0.10)',
            letterSpacing: '0.5px',
          }}>
            {deleteMsg}
          </div>
        )}
        <h2 style={{ textAlign: "center", color: "#a67c52", fontWeight: 800, marginBottom: "2.5rem" }}>
          {eventFilter ? `Feedback for: ${eventFilter}` : 'All Feedback'}
        </h2>
        {feedbacks.length === 0 ? (
          <div style={{ textAlign: "center", color: "#888", fontSize: "1.2rem" }}>
            No feedback submitted yet.
          </div>
        ) : (
          <div style={{ display: "flex", flexWrap: "wrap", gap: "2rem", justifyContent: "center" }}>
            {feedbacks.map((fb, idx) => (
              <div
                key={idx}
                style={{
                  background: "rgba(255,255,255,0.97)",
                  borderRadius: "22px",
                  boxShadow: "0 4px 24px rgba(30, 64, 175, 0.10)",
                  padding: "1.5rem 2rem",
                  minWidth: 270,
                  maxWidth: 340,
                  width: "100%",
                  border: "1.5px solid #e9dbc7",
                  position: "relative",
                }}
              >
                <div style={{ fontWeight: 700, color: "#a67c52", fontSize: "1.1rem", marginBottom: 8 }}>
                  Name: <span style={{ color: "#333" }}>{fb.name}</span>
                </div>
                <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8 }}>
                  Event: <span style={{ color: "#333" }}>{fb.event}</span>
                </div>
                <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8, display: 'flex', alignItems: 'center', gap: 4 }}>
                  Rating:
                  <span style={{ fontSize: '1.3rem', color: '#ffb400', marginLeft: 4 }}>
                    {Array.from({ length: fb.rating }, (_, i) => (
                      <span key={i}>★</span>
                    ))}
                  </span>
                </div>
                <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8 }}>
                  Comments:
                  <div style={{ color: "#333", fontWeight: 400, marginTop: 2 }}>{fb.comments || <span style={{color:'#bbb'}}>No comments</span>}</div>
                </div>
                {adminUnlocked && (
                  <button
                    onClick={() => handleDelete(idx)}
                    style={{
                      position: 'absolute',
                      top: 10,
                      right: 10,
                      background: 'linear-gradient(90deg, #c0392b 0%, #e67e22 100%)',
                      color: '#fff',
                      border: 'none',
                      borderRadius: '50%',
                      width: 32,
                      height: 32,
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '1.2rem',
                      boxShadow: '0 1px 4px rgba(192,57,43,0.10)',
                      cursor: 'pointer',
                      transition: 'background 0.2s, transform 0.13s',
                    }}
                    title="Delete Feedback"
                    aria-label="Delete Feedback"
                  >
                    <span role="img" aria-label="delete">🗑️</span>
                  </button>
                )}
              </div>
            ))}
          </div>
        )}
        {/* Admin password modal for delete access */}
        {showAdminLogin && (
          <div style={{
            position: 'fixed',
            top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(0,0,0,0.25)',
            zIndex: 2000,
            display: 'flex', alignItems: 'center', justifyContent: 'center',
          }}>
            <div style={{
              background: '#fff',
              borderRadius: 16,
              boxShadow: '0 4px 24px rgba(30, 64, 175, 0.13)',
              padding: '2.2rem 2rem',
              minWidth: 320,
              maxWidth: 380,
              width: '100%',
              textAlign: 'center',
              position: 'relative',
            }}>
              <div style={{fontWeight:800, fontSize:'1.2rem', color:'#a67c52', marginBottom:16}}>أدخل كلمة مرور الأدمن</div>
              <input
                type="password"
                value={adminPassword}
                onChange={e => setAdminPassword(e.target.value)}
                placeholder="كلمة المرور"
                style={{
                  width: '100%',
                  padding: '0.9rem',
                  borderRadius: 10,
                  border: '1.5px solid #e9dbc7',
                  fontSize: '1.1rem',
                  marginBottom: 14,
                  outline: 'none',
                  background: '#f7f3e9',
                  transition: 'border-color 0.2s',
                }}
                onFocus={e => e.target.style.border = '1.5px solid #a67c52'}
                onBlur={e => e.target.style.border = '1.5px solid #e9dbc7'}
                autoFocus
              />
              {adminError && <div style={{color:'#c00', marginBottom:10}}>{adminError}</div>}
              <div style={{display:'flex', gap:10, justifyContent:'center'}}>
                <button
                  onClick={() => {
                    if (adminPassword === ADMIN_PASSWORD) {
                      setShowAdminLogin(false);
                      setAdminUnlocked(true);
                      setAdminPassword("");
                      setAdminError("");
                    } else {
                      setAdminError('كلمة المرور غير صحيحة');
                    }
                  }}
                  style={{
                    background: 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)',
                    color: '#fff',
                    border: 'none',
                    borderRadius: 999,
                    fontWeight: 700,
                    fontSize: '1.1rem',
                    padding: '0.7rem 1.5rem',
                    cursor: 'pointer',
                  }}
                >دخول</button>
                <button
                  onClick={() => { setShowAdminLogin(false); setAdminPassword(""); setAdminError(""); setAdminUnlocked(false); }}
                  style={{
                    background: '#eee',
                    color: '#a67c52',
                    border: 'none',
                    borderRadius: 999,
                    fontWeight: 700,
                    fontSize: '1.1rem',
                    padding: '0.7rem 1.5rem',
                    cursor: 'pointer',
                  }}
                >إلغاء</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminViewFeedback;
