import React, { useState } from "react";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Container,
  Alert,
  Row,
  Col,
} from "reactstrap";

const Feed = () => {
  const [formData, setFormData] = useState({
    name: "",
    event: "",
    rating: "",
    comments: "",
  });

  const [submitted, setSubmitted] = useState(false);
  const [lang, setLang] = useState('en');

  const t = {
    en: {
      formTitle: 'Event Evaluation Form',
      yourName: 'Your Name',
      namePlaceholder: 'Enter your full name',
      eventName: 'Event Name',
      selectEvent: '-- Select an Event --',
      engineeringDay: 'Engineering Day',
      orientationSession: 'Orientation Session',
      workshop: 'Workshop',
      rating: 'Rating (1 to 5)',
      comments: 'Additional Comments',
      commentsPlaceholder: 'Share your thoughts...',
      submit: 'Submit Evaluation',
      thankYou: 'Thank you for your feedback!',
      help: 'Your opinion helps us improve our events.',
      chooseLang: 'عربي',
    },
    ar: {
      formTitle: 'نموذج تقييم الفعالية',
      yourName: 'اسمك',
      namePlaceholder: 'أدخل اسمك الكامل',
      eventName: 'اسم الفعالية',
      selectEvent: '-- اختر فعالية --',
      engineeringDay: 'يوم الهندسة',
      orientationSession: 'جلسة تعريفية',
      workshop: 'ورشة عمل',
      rating: 'التقييم (1 إلى 5)',
      comments: 'تعليقات إضافية',
      commentsPlaceholder: 'شاركنا رأيك...',
      submit: 'إرسال التقييم',
      thankYou: 'شكراً لملاحظاتك!',
      help: 'رأيك يساعدنا في تحسين فعالياتنا.',
      chooseLang: 'English',
    }
  };

  const toggleLang = () => setLang(lang === 'en' ? 'ar' : 'en');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted:", formData);
    setSubmitted(true);
  };

  if (submitted) {
    return (
      <Container
        style={{
          minHeight: '100vh',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <Row className="justify-content-center">
          <Col md={8} lg={6}>
            <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
              <Button
                size="sm"
                color="secondary"
                onClick={toggleLang}
                style={{
                  borderRadius: '20px',
                  background: 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)',
                  color: '#fff',
                  fontWeight: 600,
                  boxShadow: '0 2px 8px rgba(79, 140, 255, 0.12)',
                  border: 'none',
                  padding: '0.5rem 1.5rem',
                  transition: 'background 0.2s, box-shadow 0.2s',
                  letterSpacing: '0.5px',
                  outline: 'none',
                  cursor: 'pointer',
                }}
                onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae8 0%, #7bb6fa 100%)'}
                onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)'}
              >
                {t[lang].chooseLang}
              </Button>
            </div>
            <Alert color="success" className="text-center shadow-sm rounded">
              <h2 className="mb-3">{t[lang].thankYou}</h2>
              <p>{t[lang].help}</p>
            </Alert>
          </Col>
        </Row>
      </Container>
    );
  }

  return (
    <Container
      className="justify-content-center"
      style={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <Row className="justify-content-center">
        <Col md={8} lg={6}>
          <div
            style={{
              background: 'rgba(255,255,255,0.75)',
              borderRadius: '32px',
              boxShadow: '0 8px 40px rgba(30, 64, 175, 0.18)',
              padding: '3rem 2.5rem',
              border: '1.5px solid #e3e8f7',
              marginBottom: '2rem',
              maxWidth: '100%',
              backdropFilter: 'blur(8px)',
              fontFamily: 'Tajawal, Cairo, Roboto, sans-serif',
            }}
          >
            <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
              <Button
                size="sm"
                color="secondary"
                onClick={toggleLang}
                style={{
                  borderRadius: '20px',
                  background: 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)',
                  color: '#fff',
                  fontWeight: 600,
                  boxShadow: '0 2px 8px rgba(79, 140, 255, 0.12)',
                  border: 'none',
                  padding: '0.5rem 1.5rem',
                  transition: 'background 0.2s, box-shadow 0.2s',
                  letterSpacing: '0.5px',
                  outline: 'none',
                  cursor: 'pointer',
                }}
                onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae8 0%, #7bb6fa 100%)'}
                onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)'}
              >
                {t[lang].chooseLang}
              </Button>
            </div>
            <h2 className="text-center mb-4 text-primary" style={{fontWeight:800, fontSize:'2.2rem', letterSpacing:'1px', color:'#2a3a5e', fontFamily:'inherit'}}> {t[lang].formTitle} </h2>
            <Form onSubmit={handleSubmit}>
              <FormGroup>
                <Label for="name" style={{fontWeight:600, color:'#3a4a6a', marginBottom:'0.3rem', fontSize:'1.1rem', letterSpacing:'0.2px'}}> {t[lang].yourName}</Label>
                <Input
                  type="text"
                  name="name"
                  id="name"
                  placeholder={t[lang].namePlaceholder}
                  value={formData.name}
                  onChange={handleChange}
                  required
                  style={{
                    background: 'rgba(255,255,255,0.95)',
                    border: '2px solid #b6c7f7',
                    borderRadius: '18px',
                    padding: '1.15rem 1.2rem',
                    fontSize: '1.13rem',
                    marginBottom: '1.2rem',
                    boxShadow: '0 2px 12px rgba(79, 140, 255, 0.08)',
                    outline: 'none',
                    transition: 'border-color 0.2s, box-shadow 0.2s',
                    fontFamily: 'inherit',
                  }}
                  onFocus={e => e.currentTarget.style.border = '2px solid #357ae8'}
                  onBlur={e => e.currentTarget.style.border = '2px solid #b6c7f7'}
                />
              </FormGroup>

              <FormGroup>
                <Label for="event" style={{fontWeight:600, color:'#3a4a6a', marginBottom:'0.3rem', fontSize:'1.1rem', letterSpacing:'0.2px'}}> {t[lang].eventName}</Label>
