              </FormGroup>

              <FormGroup>
                <Label for="rating" style={{fontWeight:600, color:'#3a4a6a', marginBottom:'0.3rem', fontSize:'1.1rem', letterSpacing:'0.2px'}}> {t[lang].rating}</Label>
                <Input
                  type="number"
                  name="rating"
                  id="rating"
                  min="1"
                  max="5"
                  value={formData.rating}
                  onChange={handleChange}
                  required
                  style={{
                    background: 'rgba(255,255,255,0.95)',
                    border: '2px solid #b6c7f7',
                    borderRadius: '18px',
                    padding: '1.15rem 1.2rem',
                    fontSize: '1.13rem',
                    marginBottom: '1.2rem',
                    boxShadow: '0 2px 12px rgba(79, 140, 255, 0.08)',
                    outline: 'none',
                    transition: 'border-color 0.2s, box-shadow 0.2s',
                    fontFamily: 'inherit',
                  }}
                  onFocus={e => e.currentTarget.style.border = '2px solid #357ae8'}
                  onBlur={e => e.currentTarget.style.border = '2px solid #b6c7f7'}
                />
              </FormGroup>

              <FormGroup>
                <Label for="comments" style={{fontWeight:600, color:'#3a4a6a', marginBottom:'0.3rem', fontSize:'1.1rem', letterSpacing:'0.2px'}}> {t[lang].comments}</Label>
                <Input
                  type="textarea"
                  name="comments"
                  id="comments"
                  placeholder={t[lang].commentsPlaceholder}
                  rows="4"
                  value={formData.comments}
                  onChange={handleChange}
                  style={{
                    background: 'rgba(255,255,255,0.95)',
                    border: '2px solid #b6c7f7',
                    borderRadius: '18px',
                    padding: '1.15rem 1.2rem',
                    fontSize: '1.13rem',
                    marginBottom: '1.2rem',
                    boxShadow: '0 2px 12px rgba(79, 140, 255, 0.08)',
                    outline: 'none',
                    transition: 'border-color 0.2s, box-shadow 0.2s',
                    fontFamily: 'inherit',
                  }}
                  onFocus={e => e.currentTarget.style.border = '2px solid #357ae8'}
                  onBlur={e => e.currentTarget.style.border = '2px solid #b6c7f7'}
                />
              </FormGroup>

              <Button
                color="primary"
                block
                className="mt-3"
                style={{
                  background: 'linear-gradient(90deg, #357ae8 0%, #4f8cff 100%)',
                  border: 'none',
                  borderRadius: '24px',
                  fontWeight: 800,
                  fontSize: '1.25rem',
                  padding: '1.1rem 0',
                  boxShadow: '0 4px 16px rgba(79, 140, 255, 0.13)',
                  letterSpacing: '0.7px',
                  transition: 'background 0.2s, box-shadow 0.2s',
                  outline: 'none',
                  marginTop: '1.7rem',
                  fontFamily: 'inherit',
                  textShadow: '0 1px 2px rgba(30,64,175,0.07)',
                }}
                onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #2a3a5e 0%, #357ae8 100%)'}
                onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae8 0%, #4f8cff 100%)'}
              >
                {t[lang].submit}
              </Button>
            </Form>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Feed;
