import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import {
    FaInstagram,
    FaArrowUp,
    FaEnvelope,
    FaTwitter
} from 'react-icons/fa';
import './Footer.css';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';

const Footer = () => {
    const currentYear = new Date().getFullYear();
    const [showScroll, setShowScroll] = useState(false);

    const checkScrollTop = () => {
        if (!showScroll && window.pageYOffset > 400) {
            setShowScroll(true);
        } else if (showScroll && window.pageYOffset <= 400) {
            setShowScroll(false);
        }
    };

    const scrollTop = () => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
    };
  



    return (
        <footer className="footer">
            <div className="footer-container">
                <div className="footer-section">
                    <h3 className="footer-title">القرية الهندسية</h3>
                    <ul className="footer-links">
                        <li><Link to="https://www.ev-center.com/ar.html">الموقع الرسمي للقرية</Link></li>
                        <li><Link to="https://www.ev-center.com/career.html?lang=ar">فرص التدريب و العمل</Link></li>
                    </ul>
                </div>

               

                <div className="footer-section">
                    <h3 className="footer-title">تواصل معنا</h3>
                    <div className="social-links">
                        {/* <a href="https://github.com" target="_blank" rel="noopener noreferrer" aria-label="GitHub">
                            <FaGithub className="social-icon" />
                        </a> */}

                         <a href="https://x.com/ev_centers" target="_blank" rel="noopener noreferrer" aria-label="X">
                            <FaTwitter className="social-icon" />
                        </a> 
                        {/* <a href="https://facebook.com" target="_blank" rel="noopener noreferrer" aria-label="Facebook">
                            <FaFacebook className="social-icon" />
                        </a> */}
                        <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" aria-label="Instagram">
                            <FaInstagram className="social-icon" />
                        </a>
                        <a href="mailto:info@ev-center.com" aria-label="Email">
                            <FaEnvelope className="social-icon" />
                        </a>
                        <a></a>
                    </div>
                    <div className="contact-info">
                        <p><strong>Email:</strong> info@ev-center.com </p>
                        <p><strong>Phone:</strong> +968 91180801</p>
                        <p><strong>Address:</strong> 123 University St, Adam, Oman</p>
                    </div>
                   
                </div>
            </div>



            <button
                className={`scroll-top ${showScroll ? 'show' : ''}`}
                onClick={scrollTop}
                aria-label="Scroll to top"
            >
                <FaArrowUp />
            </button>
        </footer>
    );
};

export default Footer;
