import React, { useState } from "react";

const Feed = () => {
  const [formData, setFormData] = useState({
    name: "",
    event: "",
    rating: "",
    comments: "",
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted:", formData);
    setSubmitted(true);
  };

  if (submitted) {
    return (
      <div className="p-6 text-center">
        <h2 className="text-2xl font-bold mb-4">Thank you for your feedback!</h2>
        <p>We appreciate your input.</p>
      </div>
    );
  }

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-white rounded shadow-md">
      <h1 className="text-2xl font-bold mb-6 text-center">Event Evaluation Form</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block font-semibold mb-1">Your Name</label>
          <input
            type="text"
            name="name"
            className="w-full border rounded px-3 py-2"
            placeholder="Enter your name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-4">
          <label className="block font-semibold mb-1">Event Name</label>
          <select
            name="event"
            className="w-full border rounded px-3 py-2"
            value={formData.event}
            onChange={handleChange}
            required
          >
            <option value="">Select an event</option>
            <option value="Engineering Day">Engineering Day</option>
            <option value="Orientation Session">Orientation Session</option>
            <option value="Workshop">Workshop</option>
          </select>
        </div>

        <div className="mb-4">
          <label className="block font-semibold mb-1">Rating (1â€“5)</label>
          <input
            type="number"
            name="rating"
            min="1"
            max="5"
            className="w-full border rounded px-3 py-2"
            value={formData.rating}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-4">
          <label className="block font-semibold mb-1">Comments</label>
          <textarea
            name="comments"
            rows="4"
            className="w-full border rounded px-3 py-2"
            placeholder="Your feedback..."
            value={formData.comments}
            onChange={handleChange}
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 transition"
        >
          Submit Evaluation
        </button>
      </form>
    </div>
  );
};

export default Feed;
