import React, { useState, useEffect } from "react";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Container,
  Alert,
  Row,
  Col,
} from "reactstrap";
import { useNavigate } from "react-router-dom";
import img from '../Components/img3.JPG';
import { useLanguage } from '../Store/LanguageContext';
import logo from '../Components/lo.png';

const Feed = () => {
  const navigate = useNavigate();
  const [adminMenuOpen, setAdminMenuOpen] = React.useState(false);
  const [adminUnlocked, setAdminUnlocked] = React.useState(false);
  const [showAdminLogin, setShowAdminLogin] = React.useState(false);
  const [adminPassword, setAdminPassword] = React.useState("");
  const [adminError, setAdminError] = React.useState("");
  const ADMIN_PASSWORD = "admin123";
  const [formData, setFormData] = useState({
    name: "",
    number:"",
    event: "",
    rating: 0,
    comments: "",
  });

  const [submitted, setSubmitted] = useState(false);
  const { lang, setLang } = useLanguage();
  const [events, setEvents] = useState([]);
  const [formError, setFormError] = useState("");
  const [fieldErrors, setFieldErrors] = useState({ name: false, event: false, rating: false, comments: false });
  const [submitError, setSubmitError] = useState("");


  // Fetch events from backend
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const res = await fetch('http://localhost:3001/events');
        if (!res.ok) throw new Error('Failed to fetch events');
        const data = await res.json();
        setEvents(data);
      } catch (err) {
        // fallback to default if error
        setEvents([
          { en: "Share your opinion, because your opinion matters! â­", ar: "â­ Ø´Ø§Ø±ÙƒÙ†Ø§ Ø±Ø£ÙŠÙƒØŒ Ù„Ø£Ù† Ø±Ø£ÙŠÙƒ ÙŠÙØ±Ù‚ " },
        ]);
      }
    };
    fetchEvents();
  }, []);

  const t = {
    en: {
      formTitle: 'Share your opinion, because your opinion matters! â­',
      yourName: 'Your Name',
      namePlaceholder: 'Enter your full name',
      eventName: 'Event Name',
      selectEvent: '-- Select an Event --',
      engineeringDay: 'Engineering Day',
      orientationSession: 'Orientation Session',
      workshop: 'Workshop',
      rating: 'Rating (1 to 5)',
      comments: 'Additional Comments',
      commentsPlaceholder: 'Share your thoughts...',
      submit: 'Submit',
      thankYou: 'Thank you for your feedback!',
      help: 'Your opinion helps us improve our events.',
      yourPhone: "Your Phone Number",
      phonePlaceholder: "Enter your phone number",
      chooseLang: 'Ø¹Ø±Ø¨ÙŠ',
    },
    ar: {
      formTitle: 'â­Ø´Ø§Ø±ÙƒÙ†Ø§ Ø±Ø£ÙŠÙƒØŒ Ù„Ø£Ù† Ø±Ø£ÙŠÙƒ ÙŠÙØ±Ù‚ ',
      yourName: 'Ø§Ø³Ù…Ùƒ',
      namePlaceholder: 'Ø£Ø¯Ø®Ù„ Ø§Ø³Ù…Ùƒ Ø§Ù„ÙƒØ§Ù…Ù„',
      eventName: 'Ø§Ø³Ù… Ø§Ù„ÙØ¹Ø§Ù„ÙŠØ©',
      selectEvent: '-- Ø§Ø®ØªØ± ÙØ¹Ø§Ù„ÙŠØ© --',
      engineeringDay: 'ÙŠÙˆÙ… Ø§Ù„Ù‡Ù†Ø¯Ø³Ø©',
      orientationSession: 'Ø¬Ù„Ø³Ø© ØªØ¹Ø±ÙŠÙÙŠØ©',
      workshop: 'ÙˆØ±Ø´Ø© Ø¹Ù…Ù„',
      rating: 'Ø§Ù„ØªÙ‚ÙŠÙŠÙ… (1 Ø¥Ù„Ù‰ 5)',
      comments: 'ØªØ¹Ù„ÙŠÙ‚Ø§Øª Ø¥Ø¶Ø§ÙÙŠØ©',
      commentsPlaceholder: 'Ø´Ø§Ø±ÙƒÙ†Ø§ Ø±Ø£ÙŠÙƒ...',
      submit: 'ØªØ³Ù„ÙŠÙ… ',
      thankYou: 'Ø´ÙƒØ±Ø§Ù‹ Ù„Ù…Ù„Ø§Ø­Ø¸Ø§ØªÙƒ!',
      help: 'Ø±Ø£ÙŠÙƒ ÙŠØ³Ø§Ø¹Ø¯Ù†Ø§ ÙÙŠ ØªØ­Ø³ÙŠÙ† ÙØ¹Ø§Ù„ÙŠØ§ØªÙ†Ø§.',
      yourPhone: "Ø±Ù‚Ù… Ø§Ù„Ù‡Ø§ØªÙ",
      phonePlaceholder: "Ø£Ø¯Ø®Ù„ Ø±Ù‚Ù… Ù‡Ø§ØªÙÙƒ",
      chooseLang: 'English',
    }
  };

  const toggleLang = () => setLang(lang === 'en' ? 'ar' : 'en');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Star rating handler
  const handleStarClick = (star) => {
    setFormData((prev) => ({ ...prev, rating: star }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validation
    const errors = {
      name: !formData.name.trim(),
      number: !formData.number.trim(),
      event: !formData.event.trim(),
      rating: !formData.rating,
      comments: !formData.comments.trim(),
    };
    setFieldErrors(errors);
    const hasError = Object.values(errors).some(Boolean);
    if (hasError) return;
    // Send feedback to backend
    setSubmitError("");
    try {
      const res = await fetch('http://localhost:3001/feedback', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      if (!res.ok) {
        const data = await res.json();
        setSubmitError(data.msg || (lang === 'ar' ? 'Ø­Ø¯Ø« Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø§Ù„Ø¥Ø±Ø³Ø§Ù„' : 'Submission failed'));
        return;
      }
      setSubmitted(true);
    } catch (err) {
      setSubmitError(lang === 'ar' ? 'ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…' : 'Could not connect to server');
    }
  };

  // Redirect to feedback page after submit
  React.useEffect(() => {
    if (submitted) {
      const eventParam = encodeURIComponent(formData.event);
      navigate(`/admin/feedback?event=${eventParam}`);
    }
  }, [submitted, navigate]);

  return (
    <Container
      className="justify-content-center"
      style={{
        backgroundImage: `url(${img})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
        minHeight: '90vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontFamily: 'Tajawal, Cairo, Roboto, Arial, sans-serif',
        color: '#232336', // Ù„ÙˆÙ† Ø®Ø· Ø£Ø³Ø§Ø³ÙŠ Ø¯Ø§ÙƒÙ† ÙˆÙˆØ§Ø¶Ø­
      }}
    >
      {/* Small logo at the top left */}
      <img src={logo} alt="Engineering Village Logo" style={{ position: 'absolute', top: 18, left: 18, width: 150, height: 'auto', zIndex: 100 }} />
      {/* Admin access button and menu */}
      <div style={{ position: 'absolute', top: 24, right: 24, zIndex: 10 }}>
        <button
          onClick={() => {
            setShowAdminLogin(true);
          }}
          style={{
            background: 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)',
            color: '#fff',
            border: 'none',
            borderRadius: '50%',
            width: 48,
            height: 48,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '1.6rem',
            boxShadow: '0 2px 8px rgba(166, 124, 82, 0.10)',
            cursor: 'pointer',
            transition: 'background 0.2s, transform 0.13s',
          }}
          title={adminUnlocked ? "Admin Menu" : "Admin Login"}
          aria-label="Admin Menu"
          onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #e9dbc7 0%, #a67c52 100%)'}
          onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)'}
        >
          <span role="img" aria-label="lock">ðŸ”’</span>
        </button>
        {adminUnlocked && adminMenuOpen && (
          <div 
            style={{
              position: 'absolute',
              top: 56,
              right: 0,
              background: '#fff',
              borderRadius: 12,
              boxShadow: '0 2px 12px rgba(166, 124, 82, 0.13)',
              minWidth: 170,
              padding: '0.5rem 0',
              display: 'flex',
              flexDirection: 'column',
              gap: 0,
              fontWeight: 600,
              fontSize: '1rem',
              color: '#a67c52',
              zIndex: 100,
            }}
          >
            <button
              style={{
                background: 'none',
