import React from 'react';
import { useLanguage } from '../Store/LanguageContext';
import logo from '../Components/lo.png';
import { useNavigate, useLocation } from 'react-router-dom';

// عناوين الصفحات بناء على المسار
const titles = {
  "/":        { ar: "الرئيسية",           en: "Home" },
  "/admin/login":     { ar: "دخول الأدمن",       en: "Admin Login" },
  "/admin/add-event": { ar: "إضافة فعالية",      en: "Add Event" },
  "/admin/feedback":  { ar: "تقييمات الفعاليات", en: "Event Feedback" },
  "/event-feedback":  { ar: "ملاحظات الفعالية",  en: "Event Feedback" },
};

const Header = () => {
  const { lang, setLang } = useLanguage();
  const navigate = useNavigate();
  const location = useLocation();

  const toggleLang = () => setLang(lang === 'en' ? 'ar' : 'en');

  // استخراج الاسم من العناوين أو نص افتراضي
  const pageTitle =
    titles[location.pathname]
      ? titles[location.pathname][lang]
      : (lang === "ar" ? "..." : "...");

  // RTL (عربي): الشعار واللغة يمين والعنوان وسط - LTR (انجليزي): الشعار يسار وأدوات يمين
  const isRTL = lang === 'ar';

  return (
    <header
      style={{
        position: 'fixed',
        top: 0, left: 0, width: '100%',
        zIndex: 10000,
        backdropFilter: 'blur(8px) saturate(150%)',
        WebkitBackdropFilter: 'blur(8px) saturate(150%)',
        background: 'rgba(255,255,255,0.75)',
        borderBottom: '1.7px solid #ece2c6',
        boxShadow: '0 2px 18px 0 rgba(199, 182, 148, 0.07)',
        minHeight: 78,
        display: 'flex',
        flexDirection: isRTL ? "row-reverse" : "row",
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0 2vw',
        fontFamily: 'Tajawal, Cairo, Roboto, Arial, sans-serif',
      }}
    >
      {/* الشعار */}
      <div
        style={{
          display: 'flex', alignItems: 'center', gap: 12,
          cursor: "pointer",
          minWidth: 120,
        }}
        onClick={() => navigate("/")}
        tabIndex={0}
        aria-label="Home Page"
      >
        <img
          src={logo}
          alt="Engineering Village Logo"
          style={{
            height: 44,
            width: "auto",
            filter: "drop-shadow(0 1px 3px #d4af6e44)",
            transition: "transform 0.18s",
          }}
          onMouseOver={e=>e.currentTarget.style.transform="scale(1.08)"}
          onMouseOut ={e=>e.currentTarget.style.transform="scale(1)"}
        />
      </div>

      {/* عنوان الصفحة وسط الهيدر */}
      <div
        style={{
          textAlign: 'center',
          width: '100%',
          position: 'absolute',
          left: 0,
          right: 0,
          margin: 'auto',
          pointerEvents: 'none', // حتى لا يمنع الضغط على الأزرار
        }}
      >
        <span
          style={{
            fontWeight: 900,
            fontSize: "1.45rem",
            color: "#644819",
            letterSpacing: "0.19px",
            textShadow: "0 2px 12px #fffaf60c",
            fontFamily: "inherit",
            userSelect: "none",
            display: "inline-block"
          }}
        >
          {pageTitle}
        </span>
      </div>

      {/* أدوات الهيدر: أدمن وتغيير اللغة */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '1.1rem', minWidth: 150, justifyContent: isRTL ? "flex-start" : "flex-end" }}>
        {/* زر الأدمن */}
        <button
          onClick={() => navigate('/admin/login', { state: { from: location.pathname } })}
          style={{
            background: 'linear-gradient(90deg,#a67c52 0%,#e9dbc7 100%)',
            color: '#fff',
            border: 'none',
            borderRadius: '50%',
            width: 46, height: 46,
            display: 'flex', alignItems: 'center', justifyContent: 'center',
            fontSize: '1.43rem',
            boxShadow: '0 2px 8px rgba(166, 124, 82, 0.11)',
            cursor: 'pointer',
            transition: "background 0.16s, transform 0.13s",
          }}
          title={lang === "ar" ? "دخول الأدمن" : "Admin Login"}
          aria-label="Admin Menu"
          onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg,#e9dbc7 0%,#a67c52 100%)'}
          onMouseOut={ e => e.currentTarget.style.background = 'linear-gradient(90deg,#a67c52 0%,#e9dbc7 100%)'}
        >
          <span role="img" aria-label="lock">🔒</span>
        </button>
        {/* زر اللغة */}
        <button
          onClick={toggleLang}
          style={{
            background: 'linear-gradient(90deg, #4f8cff 0%, #7ecffb 100%)',
            color: '#fff',
            border: 'none',
            borderRadius: 23,
            padding: '0.53rem 1.32rem',
            fontWeight: 800,
            fontSize: '1.09rem',
            letterSpacing: '0.5px',
            boxShadow: '0 2px 8px rgba(79, 140, 255, 0.10)',
            cursor: 'pointer',
            outline: "none"
          }}
          onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae8 0%, #57b6e7 100%)'}
          onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #4f8cff 0%, #7ecffb 100%)'}
        >
          {lang === 'ar' ? 'English' : 'عربي'}
        </button>
      </div>
    </header>
  );
};

export default Header;
