import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useLanguage } from '../Store/LanguageContext';

const AdminViewFeedback = () => {
  const [feedbacks, setFeedbacks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [fetchError, setFetchError] = useState("");
  const [deleteError, setDeleteError] = useState("");
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const eventFilter = params.get('event');
  const [deleteMsg, setDeleteMsg] = useState("");
  const [adminUnlocked, setAdminUnlocked] = useState(true);
  const { lang } = useLanguage();
  const navigate = useNavigate();
  const [search, setSearch] = useState("");
  const [openGroups, setOpenGroups] = useState({});
  const [eventsList, setEventsList] = useState([]);


  useEffect(() => {
    fetch('http://localhost:3001/events')
      .then(res => res.json())
      .then(setEventsList)
      .catch(() => setEventsList([]));
  }, [lang]);

  useEffect(() => {
    setLoading(true);
    setFetchError("");
    fetch('http://localhost:3001/feedback')
      .then(res => res.json())
      .then(data => {
        let all = data;
        if (eventFilter) {
          all = all.filter(fb => fb.event === eventFilter);
        }
        setFeedbacks(all);
        setLoading(false);
      })
      .catch(() => {
        setFetchError(lang === 'ar' ? 'ÿ™ÿπÿ∞ÿ± ÿ¨ŸÑÿ® ÿßŸÑÿ™ŸÇŸäŸäŸÖÿßÿ™ ŸÖŸÜ ÿßŸÑÿÆÿßÿØŸÖ' : 'Failed to fetch feedbacks from server');
        setLoading(false);
      });
  }, [eventFilter, lang]);

  // ÿ≠ÿ∞ŸÅ ÿ™ŸÇŸäŸäŸÖ ŸÖŸÅÿ±ÿØ
  const handleDelete = async (idx) => {
    setDeleteError("");
    if (!window.confirm(lang === 'ar' ? 'ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ Ÿáÿ∞ÿß ÿßŸÑÿ™ŸÇŸäŸäŸÖÿü' : 'Are you sure you want to delete this feedback?')) return;
    const fbToDelete = feedbacks[idx];
    try {
      const res = await fetch(`http://localhost:3001/feedback/${fbToDelete._id}`, { method: 'DELETE' });
      if (!res.ok) {
        const data = await res.json();
        setDeleteError(data.msg || (lang === 'ar' ? 'ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ÿ∞ŸÅ' : 'Delete failed'));
        return;
      }
      const updated = feedbacks.filter((fb, i) => i !== idx);
      setFeedbacks(updated);
      setDeleteMsg(document.documentElement.dir === 'rtl' || document.body.dir === 'rtl' ? 'ÿ™ŸÖ ÿßŸÑÿ≠ÿ∞ŸÅ ÿ®ŸÜÿ¨ÿßÿ≠' : 'Deleted successfully');
      setTimeout(() => setDeleteMsg(""), 1500);
    } catch (err) {
      setDeleteError(lang === 'ar' ? 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ' : 'Could not connect to server');
    }
  };

  // ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿπÿßŸÑŸäÿ© ŸÜŸÅÿ≥Ÿáÿß ŸÖÿπ ŸÉŸÑ ÿ™ŸÇŸäŸäŸÖÿßÿ™Ÿáÿß (ÿ≠ÿ™Ÿâ ŸÑŸà ŸÖÿß ŸÅŸäŸáÿß ÿ™ŸÇŸäŸäŸÖÿßÿ™)
  const handleDeleteEvent = async (eventName) => {
    if (!window.confirm(lang === 'ar'
      ? 'ÿ≥Ÿäÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿπÿßŸÑŸäÿ© Ÿàÿ¨ŸÖŸäÿπ ÿ™ŸÇŸäŸäŸÖÿßÿ™Ÿáÿß ŸÜŸáÿßÿ¶ŸäÿßŸã. ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØÿü'
      : 'This will delete the event and all its feedback forever. Are you sure?')) return;
    try {
      // ÿßÿ≠ÿ∞ŸÅ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ŸÇŸäŸäŸÖÿßÿ™ ŸÑŸà ŸÅŸäŸá
      const promises = feedbacks
        .filter(fb => fb.event === eventName)
        .map(fb =>
          fetch(`http://localhost:3001/feedback/${fb._id}`, { method: 'DELETE' })
        );
      await Promise.all(promises);

      // ÿßÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿπÿßŸÑŸäÿ© ŸÜŸÅÿ≥Ÿáÿß ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const eventObj = eventsList.find(ev =>
        (lang === 'ar' ? ev.ar : ev.en) === eventName
      );
      if (eventObj) {
        const res = await fetch(`http://localhost:3001/events/${eventObj._id}`, { method: 'DELETE' });
        if (!res.ok) throw new Error('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿπŸÜÿØ ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿπÿßŸÑŸäÿ©!');
      }

      setEventsList(eventsList.filter(ev =>
        (lang === 'ar' ? ev.ar : ev.en) !== eventName
      ));
      setFeedbacks(feedbacks.filter(fb => fb.event !== eventName));

      setDeleteMsg(document.documentElement.dir === 'rtl' || document.body.dir === 'rtl'
        ? 'ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿπÿßŸÑŸäÿ© Ÿàÿ¨ŸÖŸäÿπ ÿ™ŸÇŸäŸäŸÖÿßÿ™Ÿáÿß'
        : 'Event and all its feedback deleted');
      setTimeout(() => setDeleteMsg(""), 1500);
    } catch (err) {
      setDeleteError(lang === 'ar' ? 'ÿ™ÿπÿ∞ÿ± ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿπÿßŸÑŸäÿ©' : 'Could not delete the event');
    }
  };

  // ÿ®ÿ≠ÿ´ Ÿàÿ™ÿµŸÅŸäÿ©
  const filteredFeedbacks = feedbacks.filter(fb => {
    const s = search.trim().toLowerCase();
    if (!s) return true;
    return fb.name.toLowerCase().includes(s) || fb.event.toLowerCase().includes(s);
  });

  const allEventNames = eventsList.map(ev => lang === 'ar' ? ev.ar : ev.en);
  const grouped = allEventNames.length > 0
    ? allEventNames.map(eventName => [
        eventName,
        filteredFeedbacks.filter(fb => fb.event === eventName)
      ])
    : Object.entries(
        filteredFeedbacks.reduce((acc, fb) => {
          acc[fb.event] = acc[fb.event] || [];
          acc[fb.event].push(fb);
          return acc;
        }, {})
      );

  function highlight(text) {
    const s = search.trim();
    if (!s) return text;
    const regex = new RegExp(`(${s.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')})`, 'gi');
    const parts = String(text).split(regex);
    return parts.map((part, i) =>
      regex.test(part)
        ? <span key={i} style={{ background: '#ffe066', color: '#232336', borderRadius: 4, padding: '0 2px' }}>{part}</span>
        : part
    );
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(135deg, #f3e9dd 0%, #e9dbc7 35%, #f7f3e9 70%, #e2c9a7 100%)",
        fontFamily: "Tajawal, Cairo, Roboto, sans-serif",
        padding: "2rem 0",
        position: 'relative',
      }}>
      <button
        onClick={() => navigate('/')}
        style={{
          position: "absolute",
          top: 100,
          left: lang === "ar" ? 32 : "auto",
          right: lang === "ar" ? "auto" : 32,
          zIndex: 20,
          display: "flex",
          alignItems: "center",
          gap: 7,
          padding: "0.6rem 1.3rem 0.6rem 1.1rem",
          border: "none",
          borderRadius: 100,
          background: "rgba(255,255,255,0.85)",
          boxShadow: "0 2px 12px 0 rgba(44,62,80,0.11)",
          fontWeight: 800,
          fontSize: "1.04rem",
          color: "#393E46",
          letterSpacing: "0.1px",
          cursor: "pointer",
          transition: "background 0.16s, box-shadow 0.16s, color 0.16s, transform 0.14s",
          backdropFilter: "blur(5px)", // ŸÑŸÖÿ≥ÿ© ÿ≤ÿ¨ÿßÿ¨Ÿäÿ©
          outline: "none",
        }}
        onMouseOver={e => {
          e.currentTarget.style.background = "rgba(225, 208, 175,0.94)";
          e.currentTarget.style.color = "#812828";
          e.currentTarget.style.transform = "scale(1.049)";
          e.currentTarget.style.boxShadow = "0 6px 18px 1px rgba(78, 52, 25, 0.18)";
        }}
        onMouseOut={e => {
          e.currentTarget.style.background = "rgba(255,255,255,0.85)";
          e.currentTarget.style.color = "#393E46";
          e.currentTarget.style.transform = "scale(1)";
          e.currentTarget.style.boxShadow = "0 2px 12px 0 rgba(44,62,80,0.11)";
        }}
      >
        {lang === "ar" ? (
          <>
            <span style={{ fontSize: "1.2em", marginRight: 5, transition: "color 0.2s" }}>{"‚Üê"}</span>
            <span>ÿßŸÑÿ±ÿ¨Ÿàÿπ</span>
          </>
        ) : (
          <>
            <span style={{ fontSize: "1.2em", marginLeft: 5, transition: "color 0.2s" }}>{"‚Üí"}</span>
            <span>Back</span>
          </>
        )}
    </button>
      <div style={{ maxWidth: 900, margin: "0 auto" }}>
        {deleteMsg && (
          <div style={{
            background: '#43a047',
            color: '#fff',
            borderRadius: 10,
            padding: '0.7rem 1.2rem',
            textAlign: 'center',
            fontWeight: 700,
            fontSize: '1.1rem',
            marginBottom: 18,
            boxShadow: '0 2px 8px rgba(67,160,71,0.10)',
            letterSpacing: '0.5px',
          }}>
            {deleteMsg}
          </div>
        )}
        {fetchError && (
          <div style={{ color: '#c00', fontWeight: 700, textAlign: 'center', marginBottom: 18 }}>
            {fetchError}
          </div>
        )}
        {deleteError && (
          <div style={{ color: '#c00', fontWeight: 700, textAlign: 'center', marginBottom: 18 }}>
            {deleteError}
          </div>
        )}
       <h2
          style={{
            textAlign: "center",
            color: "#a67c52",
            fontWeight: 800,
            marginBottom: "2.5rem",
            marginTop: "85px",  // <--- ÿßŸÑŸÖÿ≥ÿßŸÅÿ© ÿ®ŸäŸÜ ÿ≤ÿ± ÿßŸÑÿπŸàÿØÿ© (ÿßŸÑŸáŸäÿØÿ±) ŸàÿßŸÑÿπŸÜŸàÿßŸÜ
          }}
        >
          {eventFilter ? `Feedback for: ${eventFilter}` : (lang === 'ar' ? 'ŸÉŸÑ ÿßŸÑÿ™ŸÇŸäŸäŸÖÿßÿ™' : 'All Feedback')}
        </h2>
        {/* ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ®ÿ≠ÿ´ */}
        <div style={{ maxWidth: 850, margin: '0 auto 2.2rem auto', textAlign: 'center' }}>
          <input
            type="text"
            value={search}
            onChange={e => setSearch(e.target.value)}
            placeholder={lang === 'ar' ? 'ÿßÿ®ÿ≠ÿ´ ÿ®ÿßÿ≥ŸÖ ÿßŸÑŸÅÿπÿßŸÑŸäÿ© ÿ£Ÿà ÿßŸÑŸÖÿ¥ÿßÿ±ŸÉ...' : 'Search by event or name...'}
            style={{
              width: '100%',
              padding: '1.1rem 1.5rem',
              borderRadius: 14,
              border: '1.5px solid #e9dbc7',
              fontSize: '1.18rem',
              outline: 'none',
              background: '#f7f3e9',
              marginBottom: 0,
              transition: 'border-color 0.2s',
              boxShadow: '0 2px 12px #b39ddb22',
            }}
            onFocus={e => e.target.style.border = '1.5px solid #a67c52'}
            onBlur={e => e.target.style.border = '1.5px solid #e9dbc7'}
          />
        </div>
        {loading ? (
          <div style={{ textAlign: "center", color: "#888", fontSize: "1.2rem" }}>
            {lang === 'ar' ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...' : 'Loading...'}
          </div>
        ) : grouped.length === 0 ? (
          <div style={{ textAlign: "center", color: "#888", fontSize: "1.2rem" }}>
            {lang === 'ar' ? 'ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÜÿ™ÿßÿ¶ÿ¨ ŸÖÿ∑ÿßÿ®ŸÇÿ©' : 'No matching results'}
          </div>
        ) : (
          grouped.map(([eventName, group], groupIdx) => {
            const isOpen = openGroups[eventName] ?? true;
            return (
              <div key={eventName} style={{ maxWidth: 850, margin: '0 auto 2.5rem auto', border: '1.5px solid #d1c4e9', borderRadius: 14, background: '#f7f3e9', boxShadow: '0 2px 12px #b39ddb22' }}>
                <div
                  onClick={() => setOpenGroups(g => ({ ...g, [eventName]: !isOpen }))}
                  style={{
                    cursor: 'pointer',
                    padding: '1.1rem 1.5rem',
                    fontWeight: 900,
                    fontSize: '1.25rem',
                    color: '#4b2996',
                    background: isOpen ? '#e3f0ff' : '#f7f3e9',
                    borderRadius: '14px 14px 0 0',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                    letterSpacing: '1px',
                    userSelect: 'none',
                    minWidth: 0,
                  }}>
                  <div
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between',
                      gap: 8,
                      position: 'relative',
                    }}>
                    <span>{eventName}</span>
                    {/* ÿ≤ÿ± ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿπÿßŸÑŸäÿ© ŸÜŸÅÿ≥Ÿáÿß Ÿäÿ®ŸÇŸâ ÿØÿßÿ¶ŸÖŸãÿß ÿ≠ÿ™Ÿâ ÿ®ÿØŸàŸÜ ÿ™ŸÇŸäŸäŸÖÿßÿ™ */}
                    <button
                      onClick={e => { e.stopPropagation(); handleDeleteEvent(eventName); }}
                      style={{
                        background: '#e53935',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '50%',
                        width: 32,
                        height: 32,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        fontSize: '1.2rem',
                        boxShadow: '0 2px 8px rgba(229,57,53,0.10)',
                        cursor: 'pointer',
                        transition: 'background 0.2s, transform 0.13s',
                        zIndex: 2,
                      }}
                      title={lang === 'ar' ? 'ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿπÿßŸÑŸäÿ©' : 'Delete Event'}
                      aria-label={lang === 'ar' ? 'ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿπÿßŸÑŸäÿ©' : 'Delete Event'}
                      onMouseOver={e => e.currentTarget.style.background = '#b71c1c'}
                      onMouseOut={e => e.currentTarget.style.background = '#e53935'}
                    >
                      <span role="img" aria-label="delete">üóëÔ∏è</span>
                    </button>
                  </div>
                  <span style={{ fontWeight: 700, fontSize: '1.1rem', color: '#1a4d8f', marginLeft: 8 }}>
                    ({group.length})
                  </span>
                  <span style={{ fontSize: '1.3rem', marginLeft: 8 }}>{isOpen ? '‚ñº' : '‚ñ∂'}</span>
                </div>
                {isOpen && (
                  <div style={{ display: "flex", flexWrap: "wrap", gap: "2rem", justifyContent: "center", padding: '1.5rem 0' }}>
                    {group.length === 0 ? (
                      <div style={{ color: "#bbb", fontWeight: 500, padding: "1.5rem", textAlign: "center" }}>
                        {lang === 'ar'
                          ? 'ŸÑÿß ŸäŸàÿ¨ÿØ ÿ™ŸÇŸäŸäŸÖÿßÿ™ ŸÑŸáÿ∞Ÿá ÿßŸÑŸÅÿπÿßŸÑŸäÿ© ÿ≠ÿßŸÑŸäÿßŸã'
                          : 'No feedback for this event yet.'}
                      </div>
                    ) : group.map((fb, idx) => (
                      <div
                        key={fb._id || idx}
                        style={{
                          background: "rgba(255,255,255,0.97)",
                          borderRadius: "22px",
                          boxShadow: "0 4px 24px rgba(30, 64, 175, 0.10)",
                          padding: "1.5rem 2rem",
                          minWidth: 270,
                          maxWidth: 340,
                          width: "100%",
                          border: "1.5px solid #e9dbc7",
                          position: "relative",
                        }}>
                        <div style={{ fontWeight: 700, color: "#a67c52", fontSize: "1.1rem", marginBottom: 8 }}>
                          Name: <span style={{ color: "#333" }}>{highlight(fb.name)}</span>
                        </div>
                        <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8 }}>
                          Phone: <span style={{ color: "#333" }}>{highlight(fb.number)}</span>
                        </div>
                        <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8 }}>
                          Event: <span style={{ color: "#333" }}>{highlight(fb.event)}</span>
                        </div>
                        <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8, display: 'flex', alignItems: 'center', gap: 4 }}>
                          Rating:
                          <span style={{ fontSize: '1.3rem', color: '#ffb400', marginLeft: 4 }}>
                            {Array.from({ length: fb.rating }, (_, i) => (
                              <span key={i}>‚òÖ</span>
                            ))}
                          </span>
                        </div>
                        <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8 }}>
                          Comments:
                          <div style={{ color: "#333", fontWeight: 400, marginTop: 2 }}>{highlight(fb.comments) || <span style={{color:'#bbb'}}>No comments</span>}</div>
                        </div>
                        {localStorage.getItem('isAdmin') === 'true' && (
                          <button
                            onClick={() => handleDelete(feedbacks.findIndex(f => f._id === fb._id))}
                            style={{
                              position: 'absolute',
                              top: 10,
                              right: 10,
                              background: 'linear-gradient(90deg, #c0392b 0%, #e67e22 100%)',
                              color: '#fff',
                              border: 'none',
                              borderRadius: '50%',
                              width: 32,
                              height: 32,
                              display: 'flex',
                              alignItems: 'center',
                              justifyContent: 'center',
                              fontSize: '1.2rem',
                              boxShadow: '0 1px 4px rgba(192,57,43,0.10)',
                              cursor: 'pointer',
                              transition: 'background 0.2s, transform 0.13s',
                            }}
                            title="Delete Feedback"
                            aria-label="Delete Feedback"
                          >
                            <span role="img" aria-label="delete">üóëÔ∏è</span>
                          </button>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default AdminViewFeedback;
