import React, { useState } from "react";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Container,
  Alert,
  Row,
  Col,
} from "reactstrap";

const Feed = () => {
  const [formData, setFormData] = useState({
    name: "",
    event: "",
    rating: "",
    comments: "",
  });

  const [submitted, setSubmitted] = useState(false);
  const [lang, setLang] = useState('en');

  const t = {
    en: {
      formTitle: 'Event Evaluation Form',
      yourName: 'Your Name',
      namePlaceholder: 'Enter your full name',
      eventName: 'Event Name',
      selectEvent: '-- Select an Event --',
      engineeringDay: 'Engineering Day',
      orientationSession: 'Orientation Session',
      workshop: 'Workshop',
      rating: 'Rating (1 to 5)',
      comments: 'Additional Comments',
      commentsPlaceholder: 'Share your thoughts...',
      submit: 'Submit Evaluation',
      thankYou: 'Thank you for your feedback!',
      help: 'Your opinion helps us improve our events.',
      chooseLang: 'عربي',
    },
    ar: {
      formTitle: 'نموذج تقييم الفعالية',
      yourName: 'اسمك',
      namePlaceholder: 'أدخل اسمك الكامل',
      eventName: 'اسم الفعالية',
      selectEvent: '-- اختر فعالية --',
      engineeringDay: 'يوم الهندسة',
      orientationSession: 'جلسة تعريفية',
      workshop: 'ورشة عمل',
      rating: 'التقييم (1 إلى 5)',
      comments: 'تعليقات إضافية',
      commentsPlaceholder: 'شاركنا رأيك...',
      submit: 'إرسال التقييم',
      thankYou: 'شكراً لملاحظاتك!',
      help: 'رأيك يساعدنا في تحسين فعالياتنا.',
      chooseLang: 'English',
    }
  };

  const toggleLang = () => setLang(lang === 'en' ? 'ar' : 'en');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted:", formData);
    setSubmitted(true);
  };

  if (submitted) {
    return (
      <Container
        style={{
          minHeight: '100vh',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <Row className="justify-content-center">
          <Col md={8} lg={6}>
            <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
              <Button
                size="sm"
                color="secondary"
                onClick={toggleLang}
                style={{
                  borderRadius: '20px',
                  background: 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)',
                  color: '#fff',
                  fontWeight: 600,
                  boxShadow: '0 2px 8px rgba(79, 140, 255, 0.12)',
                  border: 'none',
                  padding: '0.5rem 1.5rem',
                  transition: 'background 0.2s, box-shadow 0.2s',
                  letterSpacing: '0.5px',
                  outline: 'none',
                  cursor: 'pointer',
                }}
