import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import {
    FaGithub,
    FaLinkedin,
    FaTwitter,
    FaEnvelope,
    FaFacebook,
    FaInstagram,
    FaArrowUp
} from 'react-icons/fa';
import './Footer.css';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPosts } from '../Features/PostSlice'; // Import the action to fetch posts
import { useNavigate } from 'react-router-dom';

const Footer = () => {
    const currentYear = new Date().getFullYear();
    const [showScroll, setShowScroll] = useState(false);

    const checkScrollTop = () => {
        if (!showScroll && window.pageYOffset > 400) {
            setShowScroll(true);
        } else if (showScroll && window.pageYOffset <= 400) {
            setShowScroll(false);
        }
    };

    const scrollTop = () => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
    };
    const { user, isLogin } = useSelector((state) => state.users); // Fetch user data and login status
    const posts = useSelector((state) => state.posts.posts); // Fetch posts from the Redux store
    const dispatch = useDispatch(); // Dispatch actions
    const navigate = useNavigate(); // Navigate between routes

    // Fetch posts when component loads or login status changes
    useEffect(() => {
        if (!isLogin) {
            navigate('/login'); // Redirect to login if not logged in
        } else {
            dispatch(getPosts()); // Fetch posts if logged in
        }
    }, [isLogin, navigate, dispatch]);
    window.addEventListener('scroll', checkScrollTop);

    return (
        <footer className="footer">
            <div className="footer-container">
                <div className="footer-section">
                    <h3 className="footer-title">Quick Links</h3>
                    <ul className="footer-links">
                        <li><Link to="/">Home</Link></li>
                        <li><Link to="/about">About</Link></li>
                    </ul>
                </div>

                <div className="footer-section">
                    <h3 className="footer-title">Resources</h3>
                    <ul className="footer-links">
                        <li><Link to="/feedback">Feedback</Link></li>
                        <li><Link to="/settings">Settings</Link></li>
                        <li><Link to="/profile">Profile</Link></li>
                    </ul>
                </div>

                <div className="footer-section">
                    <h3 className="footer-title">Connect With Us</h3>
                    <div className="social-links">
                        <a href="https://github.com" target="_blank" rel="noopener noreferrer" aria-label="GitHub">
                            <FaGithub className="social-icon" />
                        </a>

                        <a href="https://twitter.com" target="_blank" rel="noopener noreferrer" aria-label="Twitter">
                            <FaTwitter className="social-icon" />
                        </a>
                        <a href="https://facebook.com" target="_blank" rel="noopener noreferrer" aria-label="Facebook">
                            <FaFacebook className="social-icon" />
                        </a>
                        <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" aria-label="Instagram">
                            <FaInstagram className="social-icon" />
                        </a>
                        <a href="mailto:quicknote73@gmail.com" aria-label="Email">
                            <FaEnvelope className="social-icon" />
                        </a>
                    </div>
                    <div className="contact-info">
                        <p><strong>Email:</strong> quicknote73@gmail.com</p>
                        <p><strong>Phone:</strong> +968 91180801</p>
                        <p><strong>Address:</strong> 123 University St, Adam, Oman</p>
                    </div>
                    <div className="footer-bottom">
                        <p className="copyright">
                            © {currentYear} Peer Learning Platform. All rights reserved for Alla Alshibani and Boshra alaasmi..
                        </p>
                    </div>
                </div>
            </div>



            <button
                className={`scroll-top ${showScroll ? 'show' : ''}`}
                onClick={scrollTop}
                aria-label="Scroll to top"
            >
                <FaArrowUp />
            </button>
        </footer>
    );
};

export default Footer;
