import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useLanguage } from '../Store/LanguageContext';

const ADMIN_PASSWORD = "admin123";

const AdminLogin = () => {
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { lang, setLang } = useLanguage();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password === ADMIN_PASSWORD) {
      localStorage.setItem("isAdmin", "true");
      setLoggedIn(true);
      setError("");
    } else {
      setError("Incorrect password");
    }
  };

  // زر العودة مع تعريب بسيط
  const backBtn = (
    <button
      style={{
        width: "100%",
        padding: "0.7rem 0",
        borderRadius: "999px",
        background: "linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)",
        color: "#fff",
        fontWeight: 700,
        fontSize: "1.05rem",
        border: "none",
        boxShadow: "0 1.5px 4px rgba(166,124,82,0.13)",
        cursor: "pointer",
        marginBottom: 22,
        marginTop: 0
      }}
      onClick={() => navigate('/')}
    >
      {lang === "ar" ? "⬅️ رجوع إلى الصفحة الرئيسية" : "⬅️ Back to Home"}
    </button>
  );

  if (loggedIn) {
    return (
      <div
        style={{
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          background: "linear-gradient(135deg, #f3e9dd 0%, #e9dbc7 35%, #f7f3e9 70%, #e2c9a7 100%)",
          fontFamily: "Tajawal, Cairo, Roboto, sans-serif",
        }}
      >
        <div
          style={{
            background: "rgba(255,255,255,0.97)",
            borderRadius: "28px",
            boxShadow: "0 8px 40px rgba(30, 64, 175, 0.10)",
            padding: "2.5rem 2rem",
            border: "2px solid #e9dbc7",
            minWidth: "320px",
            maxWidth: "400px",
            width: "100%",
            textAlign: 'center',
          }}
        >
          {backBtn}
          <h2 style={{ color: "#a67c52", fontWeight: 800, marginBottom: "2rem" }}>
            مرحباً أيها الأدمن
          </h2>
          <button
            style={{
              width: "100%",
              padding: "1rem 0",
              borderRadius: "999px",
              background: "linear-gradient(90deg, #43a047 0%, #a6d1ff 100%)",
              color: "#fff",
              fontWeight: 800,
              fontSize: "1.15rem",
              border: "none",
              boxShadow: "0 2px 8px rgba(67, 160, 71, 0.10)",
              cursor: "pointer",
              marginBottom: 18,
              marginTop: 8,
              transition: "background 0.2s, transform 0.13s",
            }}
            onClick={() => navigate('/admin/add-event')}
          >
            إضافة فعالية
          </button>
          <button
            style={{
              width: "100%",
              padding: "1rem 0",
              borderRadius: "999px",
              background: "linear-gradient(90deg, #e53935 0%, #e67e22 100%)",
              color: "#fff",
              fontWeight: 800,
              fontSize: "1.15rem",
              border: "none",
              boxShadow: "0 2px 8px rgba(229,57,53,0.10)",
              cursor: "pointer",
              marginBottom: 8,
              marginTop: 8,
              transition: "background 0.2s, transform 0.13s",
            }}
            onClick={() => navigate('/admin/feedback')}
          >
            تعديل أو حذف فعالية
          </button>
        </div>
      </div>
    );
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "linear-gradient(135deg, #f3e9dd 0%, #e9dbc7 35%, #f7f3e9 70%, #e2c9a7 100%)",
        fontFamily: "Tajawal, Cairo, Roboto, sans-serif",
      }}
    >
      <div
        style={{
          background: "rgba(255,255,255,0.97)",
          borderRadius: "28px",
          boxShadow: "0 8px 40px rgba(30, 64, 175, 0.10)",
          padding: "2.5rem 2rem",
          border: "2px solid #e9dbc7",
          minWidth: "320px",
          maxWidth: "400px",
          width: "100%",
        }}
      >
        {backBtn}
        <h2 style={{ textAlign: "center", color: "#a67c52", fontWeight: 800, marginBottom: "2rem" }}>
          Admin Login
        </h2>
        <form onSubmit={handleSubmit}>
          <input
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            placeholder="Enter admin password"
            style={{
              width: "100%",
              padding: "1rem",
              borderRadius: "14px",
              border: "1.5px solid #e9dbc7",
              margin: "1rem 0 1.5rem 0",
              fontSize: "1.1rem",
              outline: "none",
              background: "#f7f3e9",
              transition: "border-color 0.2s",
            }}
            onFocus={e => e.target.style.border = '1.5px solid #a67c52'}
            onBlur={e => e.target.style.border = '1.5px solid #e9dbc7'}
            required
          />
          {error && <div style={{ color: "#c00", marginBottom: 10, textAlign: 'center' }}>{error}</div>}
          <button
            type="submit"
            style={{
              width: "100%",
              padding: "1rem 0",
              borderRadius: "999px",
              background: "linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)",
              color: "#fff",
              fontWeight: 800,
              fontSize: "1.15rem",
              border: "none",
              boxShadow: "0 2px 8px rgba(166, 124, 82, 0.10)",
              cursor: "pointer",
              transition: "background 0.2s, transform 0.13s",
            }}
            onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #e9dbc7 0%, #a67c52 100%)'}
            onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)'}
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default AdminLogin;
