// src/Pages/UserFeedbackList.js
import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { Container, Card, CardBody, CardTitle, CardText, Spinner } from "reactstrap";

const UserFeed = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const selectedEvent = queryParams.get("event");

  const [feedbacks, setFeedbacks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFeedbacks = async () => {
      try {
        const res = await fetch(`http://localhost:3001/feedback?event=${encodeURIComponent(selectedEvent)}`);
        if (!res.ok) throw new Error("Failed to fetch feedbacks");
        const data = await res.json();
        setFeedbacks(data);
      } catch (err) {
        console.error(err);
        setFeedbacks([]);
      } finally {
        setLoading(false);
      }
    };
    if (selectedEvent) fetchFeedbacks();
  }, [selectedEvent]);

  return (
    <Container className="mt-4">
      <h2 className="text-center mb-4">
        Feedback for "{selectedEvent}"
      </h2>

      {loading ? (
        <div className="text-center"><Spinner color="primary" /></div>
      ) : feedbacks.length === 0 ? (
        <p className="text-center">No feedbacks yet for this event.</p>
      ) : (
        feedbacks.map((fb, idx) => (
          <Card key={idx} className="mb-3">
            <CardBody>
              <CardTitle tag="h5">{fb.name} ({fb.number})</CardTitle>
              <CardText><strong>Rating:</strong> {fb.rating} / 5</CardText>
              <CardText><strong>Comments:</strong> {fb.comments}</CardText>
            </CardBody>
          </Card>
        ))
      )}
    </Container>
  );
};

export default UserFeed;
