import React, { useState } from "react";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Container,
  Alert,
  Row,
  Col,
} from "reactstrap";

const Feed = () => {
  const [formData, setFormData] = useState({
    name: "",
    event: "",
    rating: "",
    comments: "",
  });

  const [submitted, setSubmitted] = useState(false);
  const [lang, setLang] = useState('en');

  const t = {
    en: {
      formTitle: 'Event Evaluation Form',
      yourName: 'Your Name',
      namePlaceholder: 'Enter your full name',
      eventName: 'Event Name',
      selectEvent: '-- Select an Event --',
      engineeringDay: 'Engineering Day',
      orientationSession: 'Orientation Session',
      workshop: 'Workshop',
      rating: 'Rating (1 to 5)',
      comments: 'Additional Comments',
      commentsPlaceholder: 'Share your thoughts...',
      submit: 'Submit Evaluation',
      thankYou: 'Thank you for your feedback!',
      help: 'Your opinion helps us improve our events.',
      chooseLang: 'عربي',
    },
    ar: {
      formTitle: 'نموذج تقييم الفعالية',
      yourName: 'اسمك',
      namePlaceholder: 'أدخل اسمك الكامل',
      eventName: 'اسم الفعالية',
      selectEvent: '-- اختر فعالية --',
      engineeringDay: 'يوم الهندسة',
      orientationSession: 'جلسة تعريفية',
      workshop: 'ورشة عمل',
      rating: 'التقييم (1 إلى 5)',
      comments: 'تعليقات إضافية',
      commentsPlaceholder: 'شاركنا رأيك...',
      submit: 'إرسال التقييم',
      thankYou: 'شكراً لملاحظاتك!',
      help: 'رأيك يساعدنا في تحسين فعالياتنا.',
      chooseLang: 'English',
    }
  };

  const toggleLang = () => setLang(lang === 'en' ? 'ar' : 'en');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted:", formData);
    setSubmitted(true);
  };

  if (submitted) {
    return (
      <Container
        style={{
          minHeight: '100vh',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <Row className="justify-content-center">
          <Col md={8} lg={6}>
            <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
              <Button
                size="sm"
                color="secondary"
                onClick={toggleLang}
                style={{
                  borderRadius: '20px',
                  background: 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)',
                  color: '#fff',
                  fontWeight: 600,
                  boxShadow: '0 2px 8px rgba(79, 140, 255, 0.12)',
                  border: 'none',
                  padding: '0.5rem 1.5rem',
                  transition: 'background 0.2s, box-shadow 0.2s',
                  letterSpacing: '0.5px',
                  outline: 'none',
                  cursor: 'pointer',
                }}
                onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae8 0%, #7bb6fa 100%)'}
                onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)'}
              >
                {t[lang].chooseLang}
              </Button>
            </div>
            <Alert color="success" className="text-center shadow-sm rounded">
              <h2 className="mb-3">{t[lang].thankYou}</h2>
              <p>{t[lang].help}</p>
            </Alert>
          </Col>
        </Row>
      </Container>
    );
  }

  return (
    <Container
      className="justify-content-center"
      style={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <Row className="justify-content-center">
        <Col md={8} lg={6}>
          <div
            style={{
              background: 'linear-gradient(135deg, #fafdff 0%, #e3f0ff 100%)',
              borderRadius: '24px',
              boxShadow: '0 6px 32px rgba(30, 64, 175, 0.10)',
              padding: '2.5rem',
              border: '1px solid #d0e6ff',
              marginBottom: '2rem',
              maxWidth: '100%',
            }}
          >
            <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
              <Button
                size="sm"
                color="secondary"
                onClick={toggleLang}
                style={{
                  borderRadius: '20px',
                  background: 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)',
                  color: '#fff',
                  fontWeight: 600,
                  boxShadow: '0 2px 8px rgba(79, 140, 255, 0.12)',
                  border: 'none',
                  padding: '0.5rem 1.5rem',
                  transition: 'background 0.2s, box-shadow 0.2s',
                  letterSpacing: '0.5px',
                  outline: 'none',
                  cursor: 'pointer',
                }}
                onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae8 0%, #7bb6fa 100%)'}
                onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)'}
              >
                {t[lang].chooseLang}
              </Button>
            </div>
            <h2 className="text-center mb-4 text-primary">{t[lang].formTitle}</h2>
            <Form onSubmit={handleSubmit}>
              <FormGroup>
                <Label for="name"> {t[lang].yourName}</Label>
                <Input
                  type="text"
                  name="name"
                  id="name"
                  placeholder={t[lang].namePlaceholder}
                  value={formData.name}
                  onChange={handleChange}
                  required
                  style={{
                    background: '#fff',
                    border: '1.5px solid #4f8cff',
                    borderRadius: '12px',
                    padding: '1rem',
                    fontSize: '1.05rem',
                    marginBottom: '0.7rem',
                    boxShadow: '0 1px 4px rgba(79, 140, 255, 0.06)',
                    outline: 'none',
                    transition: 'border-color 0.2s, box-shadow 0.2s',
                  }}
                  onFocus={e => e.currentTarget.style.border = '1.5px solid #357ae8'}
                  onBlur={e => e.currentTarget.style.border = '1.5px solid #4f8cff'}
                />
              </FormGroup>

              <FormGroup>
                <Label for="event"> {t[lang].eventName}</Label>
                <Input
                  type="select"
                  name="event"
                  id="event"
                  value={formData.event}
                  onChange={handleChange}
                  required
                  style={{
                    background: '#fff',
                    border: '1.5px solid #4f8cff',
                    borderRadius: '12px',
                    padding: '1rem',
                    fontSize: '1.05rem',
                    marginBottom: '0.7rem',
                    boxShadow: '0 1px 4px rgba(79, 140, 255, 0.06)',
                    outline: 'none',
                    transition: 'border-color 0.2s, box-shadow 0.2s',
                  }}
                  onFocus={e => e.currentTarget.style.border = '1.5px solid #357ae8'}
                  onBlur={e => e.currentTarget.style.border = '1.5px solid #4f8cff'}
                >
                  <option value="">{t[lang].selectEvent}</option>
                  <option value="Engineering Day">{t[lang].engineeringDay}</option>
                  <option value="Orientation Session">{t[lang].orientationSession}</option>
                  <option value="Workshop">{t[lang].workshop}</option>
                </Input>
              </FormGroup>

              <FormGroup>
                <Label for="rating"> {t[lang].rating}</Label>
                <Input
                  type="number"
                  name="rating"
                  id="rating"
                  min="1"
                  max="5"
                  value={formData.rating}
                  onChange={handleChange}
                  required
                  style={{
                    background: '#fff',
                    border: '1.5px solid #4f8cff',
                    borderRadius: '12px',
                    padding: '1rem',
                    fontSize: '1.05rem',
                    marginBottom: '0.7rem',
                    boxShadow: '0 1px 4px rgba(79, 140, 255, 0.06)',
                    outline: 'none',
                    transition: 'border-color 0.2s, box-shadow 0.2s',
                  }}
                  onFocus={e => e.currentTarget.style.border = '1.5px solid #357ae8'}
                  onBlur={e => e.currentTarget.style.border = '1.5px solid #4f8cff'}
                />
              </FormGroup>

              <FormGroup>
                <Label for="comments">{t[lang].comments}</Label>
                <Input
                  type="textarea"
                  name="comments"
                  id="comments"
                  placeholder={t[lang].commentsPlaceholder}
                  rows="4"
                  value={formData.comments}
                  onChange={handleChange}
                  style={{
                    background: '#fff',
                    border: '1.5px solid #4f8cff',
                    borderRadius: '12px',
                    padding: '1rem',
                    fontSize: '1.05rem',
                    marginBottom: '0.7rem',
                    boxShadow: '0 1px 4px rgba(79, 140, 255, 0.06)',
                    outline: 'none',
                    transition: 'border-color 0.2s, box-shadow 0.2s',
                  }}
                  onFocus={e => e.currentTarget.style.border = '1.5px solid #357ae8'}
                  onBlur={e => e.currentTarget.style.border = '1.5px solid #4f8cff'}
                />
              </FormGroup>

              <Button
                color="primary"
                block
                className="mt-3"
                style={{
                  background: 'linear-gradient(90deg, #4f8cff 0%, #7bb6fa 100%)',
                  border: 'none',
                  borderRadius: '16px',
                  fontWeight: 700,
                  fontSize: '1.1rem',
                  padding: '0.85rem 0',
                  boxShadow: '0 2px 8px rgba(79, 140, 255, 0.10)',
                  letterSpacing: '0.5px',
                  transition: 'background 0.2s, box-shadow 0.2s',
                  outline: 'none',
                  marginTop: '1.2rem',
                }}
                onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae8 0%, #4f8cff 100%)'}
                onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #4f8cff 0%, #7bb6fa 100%)'}
              >
                {t[lang].submit}
              </Button>
            </Form>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Feed;
