import React from 'react';
import { useLanguage } from '../Store/LanguageContext';
import logo from '../Components/lo.png';
import { useNavigate, useLocation } from 'react-router-dom';

const Header = () => {
  const { lang, setLang } = useLanguage();
  const navigate = useNavigate();
  const location = useLocation();
  const toggleLang = () => setLang(lang === 'en' ? 'ar' : 'en');

  return (
    <header style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100%',
      background: 'transparent',
      boxShadow: '0 2px 12px #eee',
      zIndex: 10000,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      padding: '0.5rem 2.5rem',
      height: 70,
    }}>
      {/* Logo */}
      <img
        src={logo}
        alt="Engineering Village Logo"
        style={{ height: 48, cursor: 'pointer' }}
        onClick={() => navigate('/')}
      />
      {/* Buttons group: lock and language */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
        <button
          onClick={() => navigate('/admin/login', { state: { from: location.pathname } })}
          style={{
            background: 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)',
            color: '#fff',
            border: 'none',
            borderRadius: '50%',
            width: 48,
            height: 48,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '1.6rem',
            boxShadow: '0 2px 8px rgba(166, 124, 82, 0.10)',
            cursor: 'pointer',
            transition: 'background 0.2s, transform 0.13s',
          }}
          title={"Admin Login"}
          aria-label="Admin Menu"
          onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #e9dbc7 0%, #a67c52 100%)'}
          onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)'}
        >
          <span role="img" aria-label="lock">ðŸ”’</span>
        </button>
        <button
          onClick={toggleLang}
          style={{
            background: 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)',
            color: '#fff',
            border: 'none',
            borderRadius: 20,
            padding: '0.6rem 1.6rem',
            fontWeight: 700,
            fontSize: '1.1rem',
            boxShadow: '0 2px 8px rgba(79, 140, 255, 0.12)',
            cursor: 'pointer',
          }}
          onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae8 0%, #7bb6fa 100%)'}
          onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)'}
        >
          {lang === 'ar' ? 'English' : 'Ø¹Ø±Ø¨ÙŠ'}
        </button>
      </div>
    </header>
  );
};

export default Header;
