import React, { useState, useEffect } from "react";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Container,
  Alert,
  Row,
  Col,
} from "reactstrap";
import { useNavigate } from "react-router-dom";
import img from "../Components/img3.JPG";
import { useLanguage } from "../Store/LanguageContext";

const Feed = () => {
  const navigate = useNavigate();
  const [adminMenuOpen, setAdminMenuOpen] = useState(false);
  const { lang, t } = useLanguage();

  const [formData, setFormData] = useState({
    name: "",
    event: "",
    rating: 0,
    comments: "",
  });

  const [submitted, setSubmitted] = useState(false);

  const toggleAdminMenu = () => {
    navigate("/admin");
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleRatingChange = (rating) => {
    setFormData((prevData) => ({
      ...prevData,
      rating: rating,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:3001/feedbacks", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        setSubmitted(true);
        setFormData({ name: "", event: "", rating: 0, comments: "" });
      } else {
        alert("Error submitting feedback");
      }
    } catch (error) {
      alert("Error: " + error.message);
    }
  };

  return (
    <div
      style={{
        background: "linear-gradient(135deg, #1e3c72, #2a5298)",
        minHeight: "100vh",
        padding: "3rem 1rem",
        position: "relative",
        color: "#333",
      }}
    >
      {/* ÿ≤ÿ± ÿßŸÑÿßÿØŸÖŸÜ */}
      <Button
        onClick={toggleAdminMenu}
        style={{
          position: "fixed",
          top: "15px",
          right: "15px",
          backgroundColor: "transparent",
          border: "2px solid #fff",
          color: "#fff",
          borderRadius: "30px",
          padding: "6px 15px",
          zIndex: 1000,
          fontWeight: "bold",
        }}
      >
        üîê Admin
      </Button>

      {/* ÿßŸÑÿ¥ÿπÿßÿ± */}
      <img
        src={img}
        alt="logo"
        style={{
          maxWidth: "140px",
          borderRadius: "20px",
          display: "block",
          margin: "0 auto 1.5rem",
        }}
      />

      <Container
        style={{
          backgroundColor: "rgba(255, 255, 255, 0.95)",
          padding: "2rem",
          borderRadius: "25px",
          maxWidth: "700px",
          boxShadow: "0px 5px 15px rgba(0,0,0,0.2)",
          animation: "fadeIn 1s ease-in-out",
        }}
      >
        <h2 className="text-center mb-4">{t[lang].title}</h2>
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label>{t[lang].name}</Label>
            <Input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label>{t[lang].event}</Label>
            <Input
              type="text"
              name="event"
              value={formData.event}
              onChange={handleChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label>{t[lang].rating}</Label>
            <Row>
              {[1, 2, 3, 4, 5].map((num) => (
                <Col key={num}>
                  <Button
                    color={formData.rating === num ? "primary" : "secondary"}
                    onClick={() => handleRatingChange(num)}
                    type="button"
                    style={{
                      width: "100%",
                      borderRadius: "50%",
                    }}
                  >
                    {num}
                  </Button>
                </Col>
              ))}
            </Row>
          </FormGroup>
          <FormGroup>
            <Label>{t[lang].comments}</Label>
            <Input
              type="textarea"
              name="comments"
              value={formData.comments}
              onChange={handleChange}
              rows="4"
            />
          </FormGroup>
          <Button color="success" type="submit" block>
            {t[lang].submit}
          </Button>
        </Form>
        {submitted && (
          <Alert
            color="success"
            className="mt-3"
            style={{ animation: "fadeIn 1s" }}
          >
            {t[lang].thankYou}
          </Alert>
        )}
      </Container>

      {/* Animation CSS */}
      <style>{`
        @keyframes fadeIn {
          0% { opacity: 0; transform: translateY(10px); }
          100% { opacity: 1; transform: translateY(0); }
        }
      `}</style>
    </div>
  );
};

export default Feed;
