import React, { useState, useEffect } from "react";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Container,
  Alert,
  Row,
  Col,
} from "reactstrap";
import { useNavigate } from "react-router-dom";

const Feed = () => {
  const navigate = useNavigate();
  const [adminMenuOpen, setAdminMenuOpen] = React.useState(false);
  const isAdmin = localStorage.getItem('isAdmin') === 'true';
  const [formData, setFormData] = useState({
    name: "",
    event: "",
    rating: 0,
    comments: "",
  });

  const [submitted, setSubmitted] = useState(false);
  const [lang, setLang] = useState('en');
  const [events, setEvents] = useState([]);

  // Load events from localStorage or use defaults
  useEffect(() => {
    const stored = localStorage.getItem('events');
    if (stored) {
      setEvents(JSON.parse(stored));
    } else {
      setEvents([
        { en: "Share your opinion, because your opinion matters! ⭐", ar: "شاركنا رأيك، لأن رأيك يفرق! ⭐" },
      ]);
    }
  }, []);

  const t = {
    en: {
      formTitle: 'Share your opinion, because your opinion matters! ⭐',
      yourName: 'Your Name',
      namePlaceholder: 'Enter your full name',
      eventName: 'Event Name',
      selectEvent: '-- Select an Event --',
      engineeringDay: 'Engineering Day',
      orientationSession: 'Orientation Session',
      workshop: 'Workshop',
      rating: 'Rating (1 to 5)',
      comments: 'Additional Comments',
      commentsPlaceholder: 'Share your thoughts...',
      submit: 'Submit',
      thankYou: 'Thank you for your feedback!',
      help: 'Your opinion helps us improve our events.',
      chooseLang: 'عربي',
    },
    ar: {
      formTitle: 'شاركنا رأيك، لأن رأيك يفرق! ⭐',
      yourName: 'اسمك',
      namePlaceholder: 'أدخل اسمك الكامل',
      eventName: 'اسم الفعالية',
      selectEvent: '-- اختر فعالية --',
      engineeringDay: 'يوم الهندسة',
      orientationSession: 'جلسة تعريفية',
      workshop: 'ورشة عمل',
      rating: 'التقييم (1 إلى 5)',
      comments: 'تعليقات إضافية',
      commentsPlaceholder: 'شاركنا رأيك...',
      submit: 'تسليم ',
      thankYou: 'شكراً لملاحظاتك!',
      help: 'رأيك يساعدنا في تحسين فعالياتنا.',
      chooseLang: 'English',
    }
  };

  const toggleLang = () => setLang(lang === 'en' ? 'ar' : 'en');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Star rating handler
  const handleStarClick = (star) => {
    setFormData((prev) => ({ ...prev, rating: star }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Save feedback to localStorage
    const feedbacks = JSON.parse(localStorage.getItem('feedbacks') || '[]');
    feedbacks.push(formData);
    localStorage.setItem('feedbacks', JSON.stringify(feedbacks));
    setSubmitted(true);
  };

  // Redirect to feedback page after submit
  React.useEffect(() => {
    if (submitted) {
      const eventParam = encodeURIComponent(formData.event);
      navigate(`/admin/feedback?event=${eventParam}`);
    }
  }, [submitted, navigate]);

  return (
    <Container
      className="justify-content-center"
      style={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        background: 'linear-gradient(135deg, #f3e9dd 0%, #e9dbc7 35%, #f7f3e9 70%, #e2c9a7 100%)',
        backgroundSize: '200% 200%',
        animation: 'gradientMove 6s ease-in-out infinite',
        position: 'relative',
        overflow: 'hidden',
      }}
    >
      {/* Admin access button and menu */}
      <div style={{ position: 'absolute', top: 24, right: 24, zIndex: 10 }}>
        <button
          onClick={() => {
            if (isAdmin) {
              setAdminMenuOpen((v) => !v);
            } else {
              navigate('/admin/login');
            }
          }}
          style={{
            background: 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)',
            color: '#fff',
            border: 'none',
            borderRadius: '50%',
            width: 48,
            height: 48,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '1.6rem',
            boxShadow: '0 2px 8px rgba(166, 124, 82, 0.10)',
            cursor: 'pointer',
            transition: 'background 0.2s, transform 0.13s',
          }}
          title={isAdmin ? "Admin Menu" : "Admin Login"}
          aria-label="Admin Menu"
          onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #e9dbc7 0%, #a67c52 100%)'}
          onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)'}
        >
          <span role="img" aria-label="lock">🔒</span>
        </button>
        {isAdmin && adminMenuOpen && (
          <div
            style={{
              position: 'absolute',
              top: 56,
              right: 0,
              background: '#fff',
              borderRadius: 12,
              boxShadow: '0 2px 12px rgba(166, 124, 82, 0.13)',
              minWidth: 170,
              padding: '0.5rem 0',
              display: 'flex',
              flexDirection: 'column',
              gap: 0,
              fontWeight: 600,
              fontSize: '1rem',
              color: '#a67c52',
              zIndex: 100,
            }}
          >
            <button
              style={{
                background: 'none',
                border: 'none',
                padding: '0.7rem 1.2rem',
                textAlign: 'left',
                cursor: 'pointer',
                fontWeight: 600,
                fontSize: '1rem',
                color: '#a67c52',
                width: '100%',
                borderBottom: '1px solid #eee',
                outline: 'none',
                transition: 'background 0.15s',
              }}
              onClick={() => { setAdminMenuOpen(false); navigate('/admin/add-event'); }}
            >➕ إضافة فعالية</button>
            <button
              style={{
                background: 'none',
                border: 'none',
                padding: '0.7rem 1.2rem',
                textAlign: 'left',
                cursor: 'pointer',
                fontWeight: 600,
                fontSize: '1rem',
                color: '#a67c52',
                width: '100%',
                outline: 'none',
                transition: 'background 0.15s',
              }}
              onClick={() => { setAdminMenuOpen(false); navigate('/admin/feedback'); }}
            >📝 عرض/تعديل التقييمات</button>
          </div>
        )}
      </div>
      {/* Real AI photo background - behind the form */}
      <img
        src="https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80"
        alt="AI robot real photo"
        style={{
          position: 'absolute',
          left: '2vw',
          top: '28%',
          width: '180px',
          opacity: 0.13,
          filter: 'blur(2px)',
          zIndex: 0,
          pointerEvents: 'none',
          borderRadius: '18px',
        }}
      />
      {/* Full-screen background child image */}
      <img
        src="https://media.istockphoto.com/id/1922365650/photo/smiling-boy-in-headphones-holding-tablet-on-orange-background.jpg?b=1&s=612x612&w=0&k=20&c=6QwQwQwQwQwQwQwQwQwQwQwQwQwQwQwQwQwQwQwQwQ="
        alt="Thinking child real photo"
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          objectFit: 'cover',
          opacity: 0.18,
          filter: 'blur(2.5px)',
          zIndex: 0,
          pointerEvents: 'none',
        }}
      />
      <style>{`
        @keyframes gradientMove {
          0% {background-position: 0% 50%;}
          50% {background-position: 100% 50%;}
          100% {background-position: 0% 50%;}
        }
      `}</style>
      <Row className="justify-content-center" style={{ position: 'relative', zIndex: 1 }}>
        <Col md={8} lg={6}>
          <div
            style={{
              background: 'linear-gradient(135deg, #e3f0ff 0%, #f9fbfd 100%)',
              borderRadius: '18px',
              boxShadow: '0 4px 24px rgba(30, 64, 175, 0.08)',
              padding: '2rem',
              border: '1px solid #b6d0f7',
              marginBottom: '2rem',
              minWidth: '800px',
              maxWidth: '1800px',
              backdropFilter: 'blur(8px)',
              fontFamily: 'Tajawal, Cairo, Roboto, sans-serif',
              position: 'relative',
              overflow: 'hidden',
            }}
          >
            <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
              <Button
                size="sm"
                color="secondary"
                onClick={toggleLang}
                style={{
                  borderRadius: '20px',
                  background: 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)',
                  color: '#fff',
                  fontWeight: 600,
                  boxShadow: '0 2px 8px rgba(79, 140, 255, 0.12)',
                  border: 'none',
                  padding: '0.5rem 1.5rem',
                  transition: 'background 0.2s, box-shadow 0.2s',
                  letterSpacing: '0.5px',
                  outline: 'none',
                  cursor: 'pointer',
                }}
                onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae8 0%, #7bb6fa 100%)'}
                onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #4f8cff 0%, #a6d1ff 100%)'}
              >
                {t[lang].chooseLang}
              </Button>
            </div>
            <h2 className="text-center mb-4 text-primary" style={{fontWeight:800, fontSize:'2.2rem', letterSpacing:'1px', color:'#ff6bcb', fontFamily:'inherit', display:'flex', alignItems:'center', justifyContent:'center', gap:'0.5rem'}}>
              <span role="img" aria-label="party">🎉</span> {t[lang].formTitle} <span role="img" aria-label="sparkle">✨</span>
            </h2>
            <div style={{textAlign:'center', color:'#a67c52', fontWeight:600, fontSize:'1.15rem', marginBottom:'1.5rem', letterSpacing:'0.2px'}}>
              {lang === 'ar'
                ? 'كل كلمة منك تصنع فرق كبير في فعالياتنا القادمة 🎯'
                : 'Every word from you makes a big difference in our upcoming events 🎯'}
            </div>
            <Form onSubmit={handleSubmit}>
              <FormGroup>
                <Label for="name" style={{fontWeight:600, color:'#ff6bcb', marginBottom:'0.3rem', fontSize:'1.1rem', letterSpacing:'0.2px', display:'flex', alignItems:'center', gap:'0.3rem'}}>
                  <span role="img" aria-label="user">🧑</span> {t[lang].yourName}
                </Label>
                <Input
                  type="text"
                  name="name"
                  id="name"
                  placeholder={t[lang].namePlaceholder}
                  value={formData.name}
                  onChange={handleChange}
                  required
                  style={{
                    background: 'rgba(255,255,255,0.99)',
                    border: '2px solid #ffb86c',
                    borderRadius: '18px',
                    padding: '1.15rem 1.2rem',
                    fontSize: '1.13rem',
                    marginBottom: '1.2rem',
                    boxShadow: '0 2px 12px rgba(255, 107, 203, 0.10)',
                    outline: 'none',
                    transition: 'border-color 0.2s, box-shadow 0.2s, transform 0.15s',
                    fontFamily: 'inherit',
                    width: '85%',
                    minWidth: '220px',
                  }}
                  onFocus={e => {e.currentTarget.style.border = '2px solid #5f6cff'; e.currentTarget.style.transform = 'scale(1.03)';}}
                  onBlur={e => {e.currentTarget.style.border = '2px solid #ffb86c'; e.currentTarget.style.transform = 'scale(1)';}}
                />
              </FormGroup>

              <FormGroup>
                <Label for="event" style={{fontWeight:600, color:'#ffb86c', marginBottom:'0.3rem', fontSize:'1.1rem', letterSpacing:'0.2px', display:'flex', alignItems:'center', gap:'0.3rem'}}>
                  <span role="img" aria-label="event">📅</span> {t[lang].eventName}
                </Label>
                <Input
                  type="select"
                  name="event"
                  id="event"
                  value={formData.event}
                  onChange={handleChange}
                  required
                  style={{
                    background: 'rgba(255,255,255,0.99)',
                    border: '2px solid #ffb86c',
                    borderRadius: '18px',
                    padding: '1.15rem 1.2rem',
                    fontSize: '1.13rem',
                    marginBottom: '1.2rem',
                    boxShadow: '0 2px 12px rgba(255, 107, 203, 0.10)',
                    outline: 'none',
                    transition: 'border-color 0.2s, box-shadow 0.2s, transform 0.15s',
                    fontFamily: 'inherit',
                    width: '85%',
                    minWidth: '220px',
                  }}
                  onFocus={e => {e.currentTarget.style.border = '2px solid #5f6cff'; e.currentTarget.style.transform = 'scale(1.03)';}}
                  onBlur={e => {e.currentTarget.style.border = '2px solid #ffb86c'; e.currentTarget.style.transform = 'scale(1)';}}
                >
                  <option value="">{t[lang].selectEvent}</option>
                  {events.map((ev, idx) => (
                    <option key={ev.en + ev.ar + idx} value={lang === 'ar' ? ev.ar : ev.en}>
                      {lang === 'ar' ? ev.ar : ev.en}
                    </option>
                  ))}
                </Input>
              </FormGroup>

              <FormGroup>
                <Label for="rating" style={{fontWeight:600, color:'#5f6cff', marginBottom:'0.3rem', fontSize:'1.1rem', letterSpacing:'0.2px', display:'flex', alignItems:'center', gap:'0.3rem'}}>
                  <span role="img" aria-label="star">⭐</span> {t[lang].rating}
                </Label>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '1.2rem', userSelect: 'none' }}>
                  {[1,2,3,4,5].map((star) => (
                    <span
                      key={star}
                      onClick={() => handleStarClick(star)}
                      style={{
                        fontSize: '2.1rem',
                        cursor: 'pointer',
                        color: star <= formData.rating ? '#ffb400' : '#e0e0e0',
                        transition: 'color 0.2s',
                        filter: star <= formData.rating ? 'drop-shadow(0 2px 6px #ffb40044)' : 'none',
                      }}
                      role="img"
                      aria-label={star + ' star'}
                    >
                      ★
                    </span>
                  ))}
                </div>
              </FormGroup>

              <FormGroup>
                <Label for="comments" style={{fontWeight:600, color:'#ff6bcb', marginBottom:'0.3rem', fontSize:'1.1rem', letterSpacing:'0.2px', display:'flex', alignItems:'center', gap:'0.3rem'}}>
                  <span role="img" aria-label="comment">💬</span> {t[lang].comments}
                </Label>
                <Input
                  type="textarea"
                  name="comments"
                  id="comments"
                  placeholder={t[lang].commentsPlaceholder}
                  rows="4"
                  value={formData.comments}
                  onChange={handleChange}
                  style={{
                    background: 'rgba(255,255,255,0.99)',
                    border: '2px solid #ff6bcb',
                    borderRadius: '18px',
                    padding: '1.15rem 1.2rem',
                    fontSize: '1.13rem',
                    marginBottom: '1.2rem',
                    boxShadow: '0 2px 12px rgba(255, 107, 203, 0.10)',
                    outline: 'none',
                    transition: 'border-color 0.2s, box-shadow 0.2s, transform 0.15s',
                    fontFamily: 'inherit',
                    width: '85%',
                    minWidth: '220px',
                  }}
                  onFocus={e => {e.currentTarget.style.border = '2px solid #ffb86c'; e.currentTarget.style.transform = 'scale(1.03)';}}
                  onBlur={e => {e.currentTarget.style.border = '2px solid #ff6bcb'; e.currentTarget.style.transform = 'scale(1)';}}
                />
              </FormGroup>

              <Button
                color="primary"
                block
                className="mt-3"
                style={{
                  background: 'linear-gradient(90deg, #4f8cff 0%, #a259ff 100%)',
                  border: 'none',
                  borderRadius: '999px',
                  fontWeight: 900,
                  fontSize: '1.35rem',
                  padding: '1.15rem 0',
                  boxShadow: '0 6px 24px rgba(79, 140, 255, 0.18)',
                  letterSpacing: '1px',
                  transition: 'background 0.2s, box-shadow 0.2s, transform 0.15s',
                  outline: 'none',
                  marginTop: '1.7rem',
                  fontFamily: 'inherit',
                  textShadow: '0 1px 2px rgba(79,140,255,0.10)',
                  width: '85%',
                  display: 'block',
                  marginLeft: 'auto',
                  marginRight: 'auto',
                }}
                onMouseOver={e => {e.currentTarget.style.background = 'linear-gradient(90deg, #a259ff 0%, #4f8cff 100%)'; e.currentTarget.style.transform = 'scale(1.07)';}}
                onMouseOut={e => {e.currentTarget.style.background = 'linear-gradient(90deg, #4f8cff 0%, #a259ff 100%)'; e.currentTarget.style.transform = 'scale(1)';}}
              >
                <span role="img" aria-label="rocket">🚀</span> {t[lang].submit}
              </Button>
            </Form>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Feed;
