import React from 'react';
import { useLanguage } from '../Store/LanguageContext';
import logo from '../Components/lo.png';
import { useNavigate, useLocation } from 'react-router-dom';

const Header = () => {
  const { lang, setLang } = useLanguage();
  const navigate = useNavigate();
  const location = useLocation();

  // تحديد اسم الصفحة الحالي، يمكنك تعديله حسب 경لاجاتك
  const pageTitles = {
    '/': lang === 'en' ? 'Home' : 'الصفحة الرئيسية',
    '/event-feedback': lang === 'en' ? 'Event Feedback' : 'التقييمات',
    '/admin/login': lang === 'en' ? 'Admin Login' : 'تسجيل دخول المشرف',
    '/admin/add-event': lang === 'en' ? 'Add Event' : 'إضافة فعالية',
    '/admin/feedback': lang === 'en' ? 'Manage Feedback' : 'إدارة التقييمات',
    '/thanks': lang === 'en' ? 'Thank You!' : 'شكرًا!',
    // أضف المزيد هنا حسب مساراتك
  };

  const currentPageTitle = pageTitles[location.pathname] || '';

  const toggleLang = () => setLang(lang === 'en' ? 'ar' : 'en');

  return (
    <header style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100%',
      zIndex: 100000,
      background: 'rgba(255,255,255,0.97)',
      backdropFilter: 'saturate(100%) blur(6px)', // تأثير زجاجي
      boxShadow: '0 4px 24px rgba(0,0,0,0.05)',
      border: '1px solid rgba(226, 216, 183, 0.13)',
      height: 72,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center', // وضع العنوان في المنتصف
      fontFamily: 'Tajawal, Cairo, Roboto, sans-serif',
    }}>
      <div style={{ 
        maxWidth: 1100, 
        width: '100%', 
        display: 'flex', 
        alignItems: 'center', 
        padding: '0 1.5rem',
        justifyContent: 'space-between',
        gap: '1.5rem'
      }}>
        {/* Logo */}
        <img
          src={logo}
          alt="Engineering Village Logo"
          style={{ 
            height: 45, 
            cursor: 'pointer', 
            transition: 'transform 0.16s ease'
          }}
          onClick={() => navigate('/')}
          onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.06)'}
          onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}
        />

        {/* Title - Centered */}
        <h2 style={{
          margin: 0,
          fontFamily: 'inherit',
          color: '#1a202c',
          fontWeight: 700,
          fontSize: '1.3rem',
          letterSpacing: '0.2px',
          background: 'linear-gradient(90deg, #5d9cecc4 0%, #a6d1ff 98%)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent', // تأثير تدرج نص
          display: 'flex',
          alignItems: 'center',
          gap: 8,
        }}>
          {currentPageTitle}
        </h2>

        {/* Action buttons */}
        <div style={{ display: 'flex', gap: '0.7rem', alignItems: 'center' }}>
          <button
            onClick={toggleLang}
            style={{
              background: 'linear-gradient(90deg, #5d9cecc4 0%, #a6d1ff 98%)',
              color: '#fff',
              padding: '0.6rem 1.6rem',
              fontWeight: 700,
              fontSize: '1rem',
              border: 'none',
              borderRadius: 24,
              cursor: 'pointer',
              boxShadow: '0 1px 6px rgba(93, 156, 236, 0.15)',
              transition: 'background 0.18s, transform 0.14s',
              display: 'flex',
              alignItems: 'center',
              gap: 8,
            }}
            onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #357ae888 0%, #7bb6fabb 98%)'}
            onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #5d9cecc4 0%, #a6d1ff 98%)'}
          >
            <span role="img" aria-label="language">🌐</span>
            {lang === 'ar' ? 'English' : 'عربي'}
          </button>
          <button
            onClick={() => navigate('/admin/login', { state: { from: location.pathname } })}
            style={{
              background: 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)',
              color: '#fff',
              border: 'none',
              borderRadius: '50%',
              width: 44,
              height: 44,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '1.5rem',
              boxShadow: '0 1px 8px rgba(166, 124, 82, 0.10)',
              cursor: 'pointer',
              transition: 'background 0.2s ease, box-shadow 0.16s ease',
            }}
            title="Admin Login"
            aria-label="Admin Menu"
            onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #e9dbc7 0%, #a67c52 100%)'}
            onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)'}
          >
            🔒
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
