import React, { useState, useEffect } from "react";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Container,
  Alert,
  Row,
  Col,
} from "reactstrap";
import { useNavigate } from "react-router-dom";
import img from '../Components/img3.JPG';
import { useLanguage } from '../Store/LanguageContext';
import logo from '../Components/logo1.png';

const Feed = () => {
  const navigate = useNavigate();
  const { lang } = useLanguage();
  const [formData, setFormData] = useState({
    name: "",
    number: "", // ✅ الرقم
    event: "",
    rating: 0,
    comments: "",
  });

  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState("");
  const [eventList, setEventList] = useState([]);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/events");
        const data = await response.json();
        setEventList(data);
      } catch (err) {
        console.error("Error fetching events:", err);
        setError("حدث خطأ أثناء جلب الفعاليات");
      }
    };

    fetchEvents();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleRatingChange = (e) => {
    setFormData((prev) => ({ ...prev, rating: parseInt(e.target.value) }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name || !formData.number || !formData.event || formData.rating === 0) {
      setError("يرجى تعبئة جميع الحقول المطلوبة.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/feedback", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setSubmitted(true);
        setFormData({ name: "", number: "", event: "", rating: 0, comments: "" });
      } else {
        const errorData = await response.json();
        setError(errorData.message || "حدث خطأ أثناء إرسال النموذج");
      }
    } catch (err) {
      console.error("Error submitting feedback:", err);
      setError("فشل الاتصال بالخادم");
    }
  };

  if (submitted) {
    return (
      <Container className="mt-5 text-center">
        <img src={logo} alt="logo" style={{ maxWidth: "150px" }} />
        <h2 className="mt-3">شكرًا لمشاركتك!</h2>
        <Button color="primary" className="mt-3" onClick={() => navigate("/")}>
          الرجوع إلى الصفحة الرئيسية
        </Button>
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <Row>
        <Col md={6}>
          <img src={img} alt="Event" className="img-fluid rounded" />
        </Col>
        <Col md={6}>
          <h2 className="mb-4">نموذج تقييم الفعالية</h2>
          {error && <Alert color="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>
            <FormGroup>
              <Label for="name">الاسم</Label>
              <Input
                type="text"
                name="name"
                id="name"
                placeholder="أدخل اسمك"
                value={formData.name}
                onChange={handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="number">الرقم</Label>
              <Input
                type="text"
                name="number"
                id="number"
                placeholder="أدخل رقمك"
                value={formData.number}
                onChange={handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="event">اختر الفعالية</Label>
              <Input
                type="select"
                name="event"
                id="event"
                value={formData.event}
                onChange={handleChange}
              >
                <option value="">اختر الفعالية</option>
                {eventList.map((event) => (
                  <option key={event._id} value={event.name}>
                    {event.name}
                  </option>
                ))}
              </Input>
            </FormGroup>

            <FormGroup>
              <Label>التقييم</Label>
              <div>
                {[1, 2, 3, 4, 5].map((num) => (
                  <Label key={num} className="me-3">
                    <Input
                      type="radio"
                      name="rating"
                      value={num}
                      checked={formData.rating === num}
                      onChange={handleRatingChange}
                    />
                    {num} ⭐
                  </Label>
                ))}
              </div>
            </FormGroup>

            <FormGroup>
              <Label for="comments">تعليق إضافي</Label>
              <Input
                type="textarea"
                name="comments"
                id="comments"
                placeholder="أضف ملاحظاتك (اختياري)"
                value={formData.comments}
                onChange={handleChange}
              />
            </FormGroup>

            <Button color="primary" type="submit">
              إرسال
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default Feed;
