import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useLanguage } from '../Store/LanguageContext';

const ADMIN_PASSWORD = "admin123";

const AdminLogin = () => {
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const navigate = useNavigate();
  const { lang } = useLanguage();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password === ADMIN_PASSWORD) {
      localStorage.setItem("isAdmin", "true");
      setLoggedIn(true);
      setError("");
    } else {
      setError(lang === "ar" ? "كلمة المرور غير صحيحة" : "Incorrect password");
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(135deg, #f4eee7 0%, #f1ede9 100%)",
        fontFamily: "Tajawal, Cairo, Roboto, Arial, sans-serif",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        position: "relative",
      }}
    >

      {/* زر العودة دائري عائم أعلى اليسار/اليمين */}
      <button
        onClick={() => navigate('/')}
        style={{
          position: "fixed",
          top: 40,
          left: lang === "ar" ? 40 : "auto",
          right: lang === "ar" ? "auto" : 40,
          zIndex: 40,
          height: 42,
          width: 42,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          border: "none",
          borderRadius: "50%",
          background: "rgba(166,124,82,0.96)",
          boxShadow: "0 4px 15px 0 #e9dbc764",
          color: "#fff",
          fontWeight: 800,
          fontSize: "1.45rem",
          letterSpacing: "0.1px",
          cursor: "pointer",
          transition: "background 0.17s, color 0.2s, transform 0.12s",
          outline: "none",
          lineHeight: 1,
        }}
        onMouseOver={e => {
          e.currentTarget.style.background = "#866849";
          e.currentTarget.style.color = "#ffe8d1";
          e.currentTarget.style.transform = "scale(1.10)";
        }}
        onMouseOut={e => {
          e.currentTarget.style.background = "rgba(166,124,82,0.96)";
          e.currentTarget.style.color = "#fff";
          e.currentTarget.style.transform = "scale(1)";
        }}
        aria-label={lang === "ar" ? "الرجوع للواجهة" : "Back to home"}
      >
        {lang === "ar" ? "←" : "→"}
      </button>

      <main
        style={{
          background: "#fff",
          borderRadius: "24px",
          boxShadow: "0 6px 36px 0 rgba(84,45,10,0.085)",
          padding: "2.8rem 2.1rem 2.2rem",
          border: "1.5px solid #e9dbc7",
          minWidth: 330,
          maxWidth: 370,
          width: "100%",
          textAlign: "center",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        {loggedIn ? (
          <>
            <h2 style={{
              color: "#a67c52",
              fontWeight: 900,
              fontSize: "1.35rem",
              marginBottom: "1.5rem"
            }}>
              {lang === "ar" ? 'مرحبًا أيها الأدمن' : 'Welcome, Admin'}
            </h2>
            <button
              style={{
                width: "100%",
                padding: "1rem 0",
                borderRadius: "999px",
                background: "linear-gradient(90deg, #43a047 0%, #a67c52 100%)",
                color: "#fff",
                fontWeight: 800,
                fontSize: "1.1rem",
                border: "none",
                boxShadow: "0 2px 8px rgba(67, 160, 71, 0.14)",
                cursor: "pointer",
                marginBottom: 15,
                marginTop: 6,
                transition: "background 0.2s, transform 0.13s",
              }}
              onClick={() => navigate('/admin/add-event')}
            >
              {lang === "ar" ? "إضافة فعالية" : "Add Event"}
            </button>
            <button
              style={{
                width: "100%",
                padding: "1rem 0",
                borderRadius: "999px",
                background: "linear-gradient(90deg, #ce2323 0%, #e67e22 95%)",
                color: "#fff",
                fontWeight: 800,
                fontSize: "1.07rem",
                border: "none",
                boxShadow: "0 2px 8px rgba(229,57,53,0.15)",
                cursor: "pointer",
                marginBottom: 5,
                marginTop: 6,
                transition: "background 0.2s, transform 0.13s",
              }}
              onClick={() => navigate('/admin/feedback')}
            >
              {lang === "ar" ? "تعديل أو حذف فعالية" : "Edit or Delete Event"}
            </button>
          </>
        ) : (
          <>
            <h2 style={{
              color: "#a67c52",
              fontWeight: 900,
              fontSize: "1.25rem",
              marginBottom: "2rem",
              letterSpacing: "0.11px"
            }}>
              {lang === "ar" ? "تسجيل دخول الأدمن" : "Admin Login"}
            </h2>
            <form onSubmit={handleSubmit} style={{ width: "100%" }}>
              <input
                type="password"
                value={password}
                onChange={e => setPassword(e.target.value)}
                placeholder={lang === "ar" ? "ادخل كلمة المرور..." : "Enter admin password..."}
                style={{
                  width: "100%",
                  padding: "1rem 1rem",
                  borderRadius: "13px",
                  border: "1.3px solid #e9dbc7",
                  margin: "1rem 0 1.45rem 0",
                  fontSize: "1.09rem",
                  outline: "none",
                  background: "#fbfaf8",
                  letterSpacing: "0.09em",
                  transition: "border-color 0.2s, box-shadow 0.24s",
                  boxShadow: "0 1px 7px 0 rgba(198,167,130,0.07)",
                  direction: lang === "ar" ? "rtl" : "ltr"
                }}
                onFocus={e => e.target.style.border = '1.3px solid #a67c52'}
                onBlur={e => e.target.style.border = '1.3px solid #e9dbc7'}
                required
              />
              {error && <div style={{
                color: "#c02626",
                marginBottom: 12,
                textAlign: 'center',
                fontWeight: 700,
                fontSize: "1rem",
              }}>{error}</div>}
              <button
                type="submit"
                style={{
                  width: "100%",
                  padding: "1rem 0",
                  borderRadius: "999px",
                  background: "linear-gradient(90deg, #a67c52 15%, #e9dbc7 100%)",
                  color: "#fff",
                  fontWeight: 800,
                  fontSize: "1.17rem",
                  border: "none",
                  boxShadow: "0 1px 6px rgba(166, 124, 82, 0.11)",
                  cursor: "pointer",
                  transition: "background 0.18s, transform 0.13s",
                }}
                onMouseOver={e => e.currentTarget.style.background = 'linear-gradient(90deg, #e9dbc7 0%, #a67c52 100%)'}
                onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #a67c52 15%, #e9dbc7 100%)'}
              >
                {lang === "ar" ? "دخول" : "Login"}
              </button>
            </form>
          </>
        )}
      </main>
    </div>
  );
};

export default AdminLogin;
