import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { useLanguage } from '../Store/LanguageContext';
import { useNavigate } from 'react-router-dom';

const AdminViewFeedback = () => {
  const [feedbacks, setFeedbacks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [fetchError, setFetchError] = useState("");
  const [deleteError, setDeleteError] = useState("");
  const [deleteMsg, setDeleteMsg] = useState("");
  const [eventList, setEventList] = useState([]);
  const [showAdminLogin, setShowAdminLogin] = useState(false);
  const [adminPassword, setAdminPassword] = useState("");
  const [adminError, setAdminError] = useState("");
  const ADMIN_PASSWORD = 'admin123';

  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const eventFilter = params.get('event');
  const { lang } = useLanguage();
  const navigate = useNavigate();
  const [search, setSearch] = useState("");
  const [openGroups, setOpenGroups] = useState({});
  const [adminUnlocked, setAdminUnlocked] = useState(localStorage.getItem("isAdmin") === "true");

  // جلب قائمة الفعاليات
  useEffect(() => {
    fetch('http://localhost:3001/events')
      .then(res => res.json())
      .then(setEventList)
      .catch(() => setEventList([]));
  }, [lang]);

  // جلب قائمة التقييمات
  useEffect(() => {
    setLoading(true);
    setFetchError("");
    fetch('http://localhost:3001/feedback')
      .then(res => res.json())
      .then(data => {
        let all = data;
        if (eventFilter) {
          all = all.filter(fb => fb.event === eventFilter);
        }
        setFeedbacks(all);
        setLoading(false);
      })
      .catch(() => {
        setFetchError(lang === 'ar' ? 'تعذر جلب التقييمات من الخادم' : 'Failed to fetch feedbacks from server');
        setLoading(false);
      });
  }, [eventFilter, lang]);

  // حذف التقييم المفرد
  const handleDelete = async (feedbackId) => {
    setDeleteError("");
    if (!window.confirm(lang === 'ar' ? 'هل أنت متأكد من حذف هذا التقييم؟' : 'Are you sure you want to delete this feedback?')) return;
    try {
      const res = await fetch(`http://localhost:3001/feedback/${feedbackId}`, { method: 'DELETE' });
      if (!res.ok) {
        const data = await res.json();
        setDeleteError(data.msg || (lang === 'ar' ? 'فشل الحذف' : 'Delete failed'));
        return;
      }
      // Remove from UI
      const updated = feedbacks.filter(fb => fb._id !== feedbackId);
      setFeedbacks(updated);
      setDeleteMsg(document.documentElement.dir === 'rtl' || document.body.dir === 'rtl' ? 'تم الحذف بنجاح' : 'Deleted successfully');
      setTimeout(() => setDeleteMsg(""), 1500);
    } catch (err) {
      setDeleteError(lang === 'ar' ? 'تعذر الاتصال بالخادم' : 'Could not connect to server');
    }
  };

  // فلترة التقييمات حسب البحث
  const filteredFeedbacks = feedbacks.filter(fb => {
    const s = search.trim().toLowerCase();
    if (!s) return true;
    return fb.name.toLowerCase().includes(s) || fb.event.toLowerCase().includes(s);
  });

  // أسماء كل الفعاليات بلغة الواجهة
  const allEventNames = eventList.map(ev => lang === 'ar' ? ev.ar : ev.en);

  // تجميع كل الفعاليات حتى التي لا تقييمات لها
  const grouped = allEventNames.map(eventName => [
    eventName,
    filteredFeedbacks.filter(fb => fb.event === eventName)
  ]);

  // دالة تمييز نص البحث
  function highlight(text) {
    const s = search.trim();
    if (!s) return text;
    const regex = new RegExp(`(${s.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')})`, 'gi');
    const parts = String(text).split(regex);
    return parts.map((part, i) =>
      regex.test(part)
        ? <span key={i} style={{ background: '#ffe066', color: '#232336', borderRadius: 4, padding: '0 2px' }}>{part}</span>
        : part
    );
  }

  // دالة إظهار مودال دخول الإدارة
  function showAdminLoginModal() {
    setShowAdminLogin(true);
    setAdminPassword("");
    setAdminError("");
  }
  function handleAdminLogin() {
    if (adminPassword === ADMIN_PASSWORD) {
      setAdminUnlocked(true);
      localStorage.setItem('isAdmin', 'true');
      setShowAdminLogin(false);
      setAdminPassword("");
      setAdminError("");
    } else {
      setAdminError(lang === "ar" ? "كلمة المرور غير صحيحة" : "Incorrect password");
    }
  }
  function handleAdminLogout() {
    setAdminUnlocked(false);
    setShowAdminLogin(false);
    setAdminPassword("");
    setAdminError("");
    localStorage.removeItem("isAdmin");
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(135deg, #f3e9dd 0%, #e9dbc7 35%, #f7f3e9 70%, #e2c9a7 100%)",
        fontFamily: "Tajawal, Cairo, Roboto, sans-serif",
        padding: "2rem 0",
        position: 'relative',
      }}
    >
      {/* زر رجوع */}
      <button
        onClick={() => navigate('/')}
        style={{
          position: 'absolute',
          top: 24,
          left: lang === 'ar' ? undefined : 24,
          right: lang === 'ar' ? 24 : undefined,
          zIndex: 20,
          background: 'linear-gradient(90deg, #e9dbc7 0%, #a67c52 100%)',
          color: '#4b2996',
          border: 'none',
          borderRadius: '50%',
          width: 44,
          height: 44,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          fontSize: '1.7rem',
          boxShadow: '0 2px 8px rgba(166, 124, 82, 0.10)',
          cursor: 'pointer',
          transition: 'background 0.2s, transform 0.13s',
        }}
        title={lang === 'ar' ? 'الرجوع' : 'Back'}
        aria-label={lang === 'ar' ? 'الرجوع' : 'Back'}
      >
        <svg width="26" height="26" viewBox="0 0 26 26" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ transform: lang === 'ar' ? 'rotate(180deg)' : 'none' }}>
          <circle cx="13" cy="13" r="12" stroke="#4b2996" strokeWidth="2" fill="#fff" />
          <path d="M16.5 13H9.5M9.5 13L12 10.5M9.5 13L12 15.5" stroke="#4b2996" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
        </svg>
      </button>

      {/* زر تشغيل صلاحية الادمن إذا لم يكن مفتوح */}
      {!adminUnlocked && (
        <button
          onClick={showAdminLoginModal}
          style={{
            position: 'absolute',
            top: 24,
            right: 24,
            zIndex: 10,
            background: 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)',
            color: '#fff',
            border: 'none',
            borderRadius: '50%',
            width: 44,
            height: 44,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '1.4rem',
            boxShadow: '0 2px 8px rgba(166, 124, 82, 0.10)',
            cursor: 'pointer',
            transition: 'background 0.2s, transform 0.13s',
          }}
          title={lang === 'ar' ? 'دخول المصرحين' : 'Admin Login'}
          aria-label={lang === 'ar' ? 'دخول المصرحين' : 'Admin Login'}
        >
          <span role="img" aria-label="lock">🔒</span>
        </button>
      )}

      <div style={{ maxWidth: 900, margin: "0 auto" }}>
        {deleteMsg && (
          <div style={{
            background: '#43a047',
            color: '#fff',
            borderRadius: 10,
            padding: '0.7rem 1.2rem',
            textAlign: 'center',
            fontWeight: 700,
            fontSize: '1.1rem',
            marginBottom: 18,
            boxShadow: '0 2px 8px rgba(67,160,71,0.10)',
            letterSpacing: '0.5px',
          }}>
            {deleteMsg}
          </div>
        )}
        {fetchError && (
          <div style={{ color: '#c00', fontWeight: 700, textAlign: 'center', marginBottom: 18 }}>
            {fetchError}
          </div>
        )}
        {deleteError && (
          <div style={{ color: '#c00', fontWeight: 700, textAlign: 'center', marginBottom: 18 }}>
            {deleteError}
          </div>
        )}
        <h2 style={{ textAlign: "center", color: "#a67c52", fontWeight: 800, marginBottom: "2.5rem" }}>
          {eventFilter ? `Feedback for: ${eventFilter}` : (lang === "ar" ? "كل تقييمات الفعاليات" : "All Feedback")}
        </h2>
        {/* شريط البحث */}
        <div style={{ maxWidth: 850, margin: '0 auto 2.2rem auto', textAlign: 'center' }}>
          <input
            type="text"
            value={search}
            onChange={e => setSearch(e.target.value)}
            placeholder={lang === 'ar' ? 'ابحث باسم الفعالية أو المشارك...' : 'Search by event or name...'}
            style={{
              width: '100%',
              padding: '1.1rem 1.5rem',
              borderRadius: 14,
              border: '1.5px solid #e9dbc7',
              fontSize: '1.18rem',
              outline: 'none',
              background: '#f7f3e9',
              marginBottom: 0,
              transition: 'border-color 0.2s',
              boxShadow: '0 2px 12px #b39ddb22',
            }}
            onFocus={e => e.target.style.border = '1.5px solid #a67c52'}
            onBlur={e => e.target.style.border = '1.5px solid #e9dbc7'}
          />
        </div>
        {loading ? (
          <div style={{ textAlign: "center", color: "#888", fontSize: "1.2rem" }}>
            {lang === 'ar' ? 'جاري التحميل...' : 'Loading...'}
          </div>
        ) : (
           grouped.length === 0 ? (
             <div style={{ textAlign: "center", color: "#888", fontSize: "1.2rem" }}>
               {lang === 'ar' ? 'لا توجد فعاليات' : 'No events'}
             </div>
           ) : (
             grouped.map(([eventName, group], groupIdx) => {
               const isOpen = openGroups[eventName] ?? true;
               return (
                 <div key={eventName} style={{
                   maxWidth: 850,
                   margin: '0 auto 2.5rem auto',
                   border: '1.5px solid #d1c4e9',
                   borderRadius: 14,
                   background: '#f7f3e9',
                   boxShadow: '0 2px 12px #b39ddb22'
                 }}>
                   <div
                     onClick={() => setOpenGroups(g => ({ ...g, [eventName]: !isOpen }))}
                     style={{
                       cursor: 'pointer',
                       padding: '1.1rem 1.5rem',
                       fontWeight: 900,
                       fontSize: '1.25rem',
                       color: '#4b2996',
                       background: isOpen ? '#e3f0ff' : '#f7f3e9',
                       borderRadius: '14px 14px 0 0',
                       display: 'flex',
                       alignItems: 'center',
                       justifyContent: 'space-between',
                       letterSpacing: '1px',
                       userSelect: 'none',
                       minWidth: 0,
                     }}
                   >
                     <div
                       style={{
                         display: 'flex',
                         alignItems: 'center',
                         justifyContent: 'space-between',
                         gap: 8,
                         position: 'relative',
                       }}
                     >
                       <span>{eventName}</span>
                     </div>
                     <span style={{ fontWeight: 700, fontSize: '1.1rem', color: '#1a4d8f', marginLeft: 8 }}>
                       ({group.length})
                     </span>
                     <span style={{ fontSize: '1.3rem', marginLeft: 8 }}>{isOpen ? '▼' : '▶'}</span>
                   </div>
                   {isOpen && (
                     <div style={{ display: "flex", flexWrap: "wrap", gap: "2rem", justifyContent: "center", padding: '1.5rem 0' }}>
                       {group.length === 0 ? (
                         <div style={{ color: "#bbb", fontWeight: 500, padding: "1.5rem", textAlign: "center" }}>
                           {lang === 'ar'
                             ? 'لا يوجد تقييمات لهذه الفعالية حالياً'
                             : 'No feedback for this event yet.'}
                         </div>
                       ) : (
                         group.map((fb, idx) => (
                           <div
                             key={fb._id || idx}
                             style={{
                               background: "rgba(255,255,255,0.97)",
                               borderRadius: "22px",
                               boxShadow: "0 4px 24px rgba(30, 64, 175, 0.10)",
                               padding: "1.5rem 2rem",
                               minWidth: 270,
                               maxWidth: 340,
                               width: "100%",
                               border: "1.5px solid #e9dbc7",
                               position: "relative",
                             }}
                           >
                             {/* بيانات التقييم */}
                             <div style={{ fontWeight: 700, color: "#a67c52", fontSize: "1.1rem", marginBottom: 8 }}>
                               {lang === "ar" ? "الاسم" : "Name"}: <span style={{ color: "#333" }}>{highlight(fb.name)}</span>
                             </div>
                             <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8 }}>
                               {lang === "ar" ? "الجوال" : "Phone"}: <span style={{ color: "#333" }}>{highlight(fb.number)}</span>
                             </div>
                             <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8 }}>
                               {lang === "ar" ? "الفعالية" : "Event"}: <span style={{ color: "#333" }}>{highlight(fb.event)}</span>
                             </div>
                             <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8, display: 'flex', alignItems: 'center', gap: 4 }}>
                               {lang === "ar" ? "التقييم" : "Rating"}:
                               <span style={{ fontSize: '1.3rem', color: '#ffb400', marginLeft: 4 }}>
                                 {Array.from({ length: fb.rating }, (_, i) => (
                                   <span key={i}>★</span>
                                 ))}
                               </span>
                             </div>
                             <div style={{ fontWeight: 600, color: "#a67c52", fontSize: "1.05rem", marginBottom: 8 }}>
                               {lang === "ar" ? "التعليق" : "Comments"}:
                               <div style={{ color: "#333", fontWeight: 400, marginTop: 2 }}>{highlight(fb.comments) || <span style={{color:'#bbb'}}>{lang === "ar" ? "لا يوجد" : "No comments"}</span>}</div>
                             </div>
                             {/* زر الحذف يظهر للإدمن فقط */}
                             {adminUnlocked && (
                               <button
                                 onClick={() => handleDelete(fb._id)}
                                 style={{
                                   position: 'absolute',
                                   top: 10,
                                   right: 10,
                                   background: 'linear-gradient(90deg, #c0392b 0%, #e67e22 100%)',
                                   color: '#fff',
                                   border: 'none',
                                   borderRadius: '50%',
                                   width: 32,
                                   height: 32,
                                   display: 'flex',
                                   alignItems: 'center',
                                   justifyContent: 'center',
                                   fontSize: '1.2rem',
                                   boxShadow: '0 1px 4px rgba(192,57,43,0.10)',
                                   cursor: 'pointer',
                                   transition: 'background 0.2s, transform 0.13s',
                                 }}
                                 title={lang === 'ar' ? 'حذف التقييم' : 'Delete Feedback'}
                                 aria-label={lang === 'ar' ? 'حذف التقييم' : 'Delete Feedback'}
                                 onMouseOver={e => e.currentTarget.style.background = '#b71c1c'}
                                 onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #c0392b 0%, #e67e22 100%)'}
                               >
                                 <span role="img" aria-label="delete">🗑️</span>
                               </button>
                             )}
                           </div>
                         ))
                       )}
                     </div>
                   )}
                 </div>
               );
             })
           )
        )}
        {/* مودال الادمن */}
        {showAdminLogin && (
          <div style={{
            position: 'fixed',
            top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(0,0,0,0.25)',
            zIndex: 2000,
            display: 'flex', alignItems: 'center', justifyContent: 'center',
          }}>
            <div style={{
              background: '#fff',
              borderRadius: 16,
              boxShadow: '0 4px 24px rgba(30, 64, 175, 0.13)',
              padding: '2.2rem 2rem',
              minWidth: 320,
              maxWidth: 380,
              width: '100%',
              textAlign: 'center',
              position: 'relative',
            }}>
              <div style={{fontWeight:800, fontSize:'1.2rem', color:'#a67c52', marginBottom:16}}>
                {lang === "ar" ? "أدخل كلمة مرور المصرح لهم" : "Enter admin password"}
              </div>
              <input
                type="password"
                value={adminPassword}
                onChange={e => setAdminPassword(e.target.value)}
                placeholder={lang === "ar" ? "كلمة المرور" : "Password"}
                style={{
                  width: '100%',
                  padding: '0.9rem',
                  borderRadius: 10,
                  border: '1.5px solid #e9dbc7',
                  fontSize: '1.1rem',
                  marginBottom: 14,
                  outline: 'none',
                  background: '#f7f3e9',
                  transition: 'border-color 0.2s',
                }}
                onFocus={e => e.target.style.border = '1.5px solid #a67c52'}
                onBlur={e => e.target.style.border = '1.5px solid #e9dbc7'}
                autoFocus
              />
              {adminError && <div style={{color:'#c00', marginBottom:10}}>{adminError}</div>}
              <div style={{display:'flex', gap:10, justifyContent:'center'}}>
                <button
                  onClick={handleAdminLogin}
                  style={{
                    background: 'linear-gradient(90deg, #a67c52 0%, #e9dbc7 100%)',
                    color: '#fff',
                    border: 'none',
                    borderRadius: 999,
                    fontWeight: 700,
                    fontSize: '1.1rem',
                    padding: '0.7rem 1.5rem',
                    cursor: 'pointer',
                  }}
                >{lang === "ar" ? "دخول المصرح لهم" : "Admin Login"}</button>
                <button
                  onClick={() => setShowAdminLogin(false)}
                  style={{
                    background: '#eee',
                    color: '#a67c52',
                    border: 'none',
                    borderRadius: 999,
                    fontWeight: 700,
                    fontSize: '1.1rem',
                    padding: '0.7rem 1.5rem',
                    cursor: 'pointer',
                  }}
                >{lang === "ar" ? "إلغاء" : "Cancel"}</button>
              </div>
              {adminUnlocked && (
                <button
                  style={{ marginTop: 8, background: '#eee', color: '#a67c52', border: 'none', borderRadius: 999, fontWeight: 700, fontSize: '1.1rem', padding: '0.7rem 1.5rem', cursor: 'pointer' }}
                  onClick={handleAdminLogout}
                >
                  {lang === 'ar' ? 'تسجيل خروج المصرح لهم' : 'Authorized Logout'}
                </button>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminViewFeedback;
